-- L_领袖数据配置文字表.xlsx
-- id=英雄ID,name=英雄名称,desc=英雄介绍,
local DLeader = {
  [10001] = {id=10001,name="هارون الرشيد",desc="هارون الرشيد هو الخليفة العباسي من الخامس، كان يحكم الإمبراطورية العربية في ما بين 786-809 سنة. خلال فترة ولايته في التوسع الإقليمي العباسية غربا إلى أسبانيا والأناضول مباشرة الشمال، ثم شرقا، بعيدة مثل الهند. وكان آنذاك الكيان السياسي الأقوى والأكثر ازدهارا في العالم. هارلان هو زعيم موهوب جدا، وشهد عهده تطور العلم والتكنولوجيا والازدهار الثقافي."},
  [10002] = {id=10002,name="رمسيس الثاني",desc="رمسيس الثاني في مصر هو أعظم وأقوى فرعون. وجلس على العرش في سن ال 20، حكمت لأكثر من 60 عاما. رمسيس هو أيضا القائد العسكري الكبير وبناة المعلقة. وبنى عاصمة جديدة رائعة من رمسيس. ويعتقد بعض المؤرخين أيضا أن رمسيس هي القصة التوراتية موسى وفرعون فترة من مصر."},
  [10003] = {id=10003,name="هيلا سيلاسي",desc="هيلا سيلاسي المعروفة باسم 'الأسد اليهود القتال'، 'ملك الملوك' و 'مبعوث الله' هيلا سيلاسي حكم اثيوبيا منذ ما يقرب من أربعة عقود في القرن ال20. ولد في عام 1892 · · ما كانغنان تافاري، في وقت لاحق ليصبح هيلا سيلاسي، وطوال حياته للحفاظ على مصالح إثيوبيا وأفريقيا جعله على رأس النظام والجهود. هيلا سيلاسي إثيوبيا في محاولة لتعزيز التحديث والإصلاح والعلمانيين الإقطاعية المتخلفة والمشاهير، كما انه يعمل بلا كلل لإثيوبيا للحصول على مكان في الساحة والنضال الدولية."},
  [10004] = {id=10004,name="ماريا تيريزا",desc="في القرن ال18، وقاد الإمبراطورية الرومانية المقدسة وقوية هابسبورغ النمساوية من شجاعة وقوية ماريا تيريزا. هي والمعارضين لها حكم القوات المقاتلة في أوروبا، وحمايتها بشجاعة مواضيعها. في ما يقرب من 650 عاما من حكم هابسبورغ، كانت ماريا تيريزا الأول والوحيد الحاكم الإناث، لكنها تعتبر أيضا واحدة من أنجح حاكم التاريخ العائلي."},
  [10005] = {id=10005,name="سليمان القانوني",desc="كان معروفا بأنه 'الأكبر'، 'المشرع' أو 'كبير الأمير التركي' الخلافة الإسلامية في السودان والدولة العثمانية التركية. واستبعد من 1520 حتى وفاته العرش منذ 1566. وخلال فترة حكمه سليمان توسع كبير في أراضي الإمبراطورية، أوروبا، آسيا وحكام الشرق الأوسط له الخوف والخشوع."},
  [10006] = {id=10006,name="بيدرو الثاني",desc="بيدرو الثاني أول ملك البرازيلي البرازيلي المولد، وجلبت إلى البلاد في عهد الاستقرار والتنمية، وجعلها دولة قوية ومزدهرة في أمريكا الجنوبية. وهو رجل هادئ وجاد، ويقول انه هو الحكم في الحياة السياسية في البلاد، وقال انه يستخدم قوة ملكية دستورية، لصد محاولات للسيطرة جماعات سياسية قوية في البرازيل. قاد البرازيل ثلاث حروب، اكتسبت أراض جديدة في سمعة، وفي الوقت نفسه، حاول أيضا أن نركز على بناء البرازيل إلى دولة حديثة. وشجع إنتاج القهوة، وليس السكر. وتدعم بنشاط وترعى بناء الطرق والسكك الحديدية والتلغراف وكابلات تحت البحر."},
  [10007] = {id=10007,name="كازيمير الثالث",desc="كازيمير الثالث هو التاريخ البولندي الوحيد الذي حقق الفوز الملك 'عظيم' العنوان. وهو الحاكم الحازم والموهوبين، والاعتماد على مهاراته الدبلوماسية وقرارات حكيمة تحكم بولندا في القرن ال14، وترك بلاده أصبحت أكثر ثراء وقوة. الانتعاش الاقتصادي، وتعزيز الجيش والتدوين والتوسع في أراضي بولندا، كازيمير جعل بلاده أكثر ازدهارا - أيضا جعل منه واحدا من أعظم الملوك البولندية."},
  [10008] = {id=10008,name="لي تاو",desc="لي تاو المعروفة باسم 'الملك سيجونغ' لي تاو، هو الملك الرابع لمملكة جوسون، قضت كوريا الشمالية من 1418 حتى وفاته في عام 1450. يعتبر لي تاو الكثيرون في شبه الجزيرة الكورية هو أعظم ملك والرعاية والاحترام لمعيشة الشعب والحائز على جائزة، توليه منصب رفاهية الشعب وتنفيذ عدد من السياسات المدنية والاجتماعية. كما شجع سيجونغ تطوير العلوم والتكنولوجيا، وخلق الخاصة بك نظام الكتابة الكورية."},
  [10009] = {id=10009,name="هارالد غور راسموسن",desc="هارالد غور راسموسن هو بدأ الملك هارالد غور راسموسن من الدنمارك حوالي ثلاثة عقود حكم من 958 م. خلال فترة حكمه، متحدين بلوتوث الوزارات الدنمارك، والنرويجيين والألمان صد الغزو، أمر أيضا بناء العديد من قلعة من أجل تعزيز الدفاع الوطني. بلوتوث ويرجع ذلك إلى التخلي عن جهود الإيمان الأصلي الشمال التقليدية لتوجيه تحويل وطني والمشاهير إلى المسيحية أيضا."},
  [10010] = {id=10010,name="أوتو فون بسمارك",desc="أوتو فون بسمارك المعروفة باسم 'الحديد المستشارة' أوتو فون بسمارك ولعل أهم الشخصيات في التاريخ الألماني. خلال حياته السياسية الطويلة، أكملت بسمارك في توحيد ألمانيا، وإنشاء الإمبراطورية الألمانية. في كتابه لمدة 30 عاما رئاسة الوزراء، والألمانية من ضعيفة وفضفاضة المملكة تحالف تتحول إلى دولة موحدة قوية، وفي موقف وسط من الهيمنة المطلقة."},
  [10011] = {id=10011,name="ايكاترينا",desc="ايكاترينا في عهد اسرة ال18 أواخر حكم روسيا. شاهدت التوسع السريع للإمبراطورية الروسية، مع القوة العسكرية والدبلوماسية قاو مينغ إلى روسيا بنسبة عشرات الآلاف من الأميال المربعة من الأراضي. وكانت امرأة جميلة وذكية، وقالت انها تستخدم القبض سحره قلوب شخص في أوروبا الأكثر ذكاء، مما أصبحت المحكمة لها مركز أفكار جديدة في جميع أنحاء أوروبا. على الرغم من أنها ولدت في ألمانيا، كاترين العظمى من روسيا هي واحدة من أعظم الملوك."},
  [10012] = {id=10012,name="نابليون بونابرت",desc="نابليون بونابرت ولد في جزيرة كورسيكا، في المدرسة العسكرية المحلية عشرة سنة، موهوب في الرياضيات والتاريخ والجغرافيا والأدب والعلوم الإنسانية غير مبال تماما. 14 سنة تم تعيينه برتبة ملازم ثان في فوج المدفعية. عندما اندلعت الثورة الفرنسية بها، نابليون واقفا على جانب الثوار وتمت ترقيته إلى رتبة عقيد ملازم المدفعية، أسس بسرعة سمعة تاراوا."},
  [10013] = {id=10013,name="وليام أورانج",desc="اعترف الهولندي المحرر الوطني ومؤسس وليم أورانج، والمعروفة باسم 'وليم الصامت'، أطلق الانتفاضة ضد حكم هابسبورغ الإسبانية في منتصف القرن 16 م هولندا المحافظات قيادة الناس والفوز . وكان وليام الملك فيليب الثاني ملك أسبانيا في الأصل تابعة المؤمنين، ولكن العائلة المالكة الاسبانية المحافظات هولندا البروتستانت اضطهاد أثارت باستمرار سخطه الشديد، وتصبح في نهاية المطاف الزعيم الأكثر أهمية في انتفاضة الاستقلال. وليام عقد بقوة ضد النبلاء أخرى للانضمام إلى القضية، وتصبح خونة في نظر العائلة المالكة الاسبانية، قاد الجيش الهولندي في الفوز بأكثر من النصر في ساحة المعركة، لكنه توفي من رصاصة قاتل في 1584."},
  [10014] = {id=10014,name="أوغستس سيزار",desc="جوليوس سيزار الأصلي الأول الإمبراطور الروماني (يمكن أن يقال أيضا أن تكون أكبر واحد). انتهى استمر قرن من الحرب الأهلية، وخلق مستمر مائتي سنة 'السلام والازدهار' - سلام في ظل الحكم الروماني، والذي هو الثقافة الرومانية مزدهرة للغاية وفن العصر الذهبي."},
  [10015] = {id=10015,name="واشنطن",desc="واشنطن هي واحدة من العديد من كبرى الأمريكية في أواخر 1700s، الفترة الاستعمارية. على الرغم من أنه ليس المهرة، جون آدامز، بنجامين فرانكلين، ومليئة الإبداعي، ذكي أو توماس جيفرسون، ولكن واشنطن أيضا سحر الشخصية فريد - وهذا هو، بغض النظر عن القيادة في زمن الحرب أو زمن السلم. قاد فاز الجيش القارية انتصار لا يصدق في الظروف المعاكسة الشديدة، وبالتالي فإنه جلبت الاستقلال لبلاده."},
  [10016] = {id=10016,name="جنكيز خان",desc="جنكيز خان هو واحد من الزعيم الأكثر شهرة والأكثر رعبا التاريخ البشري. مئات من التماثيل، وقد صورته على المباني والبضائع، وصفت منتجات الترفيه عدد لا يحصى من أفعاله. وسيواصل نزاع الهضبة الآسيوية في القبائل البدوية الشمالية تتحد واحدة، لبناء إمبراطورية شاسعة من الأرض من أي وقت مضى. والإنجازات أقل شهرة هو مجال تصميم النظام، والأعمال التجارية والحرية الدينية في التجارة. إنه ليس مجرد قائد عسكري، كان زعيما سياسيا داهية."},
  [10017] = {id=10017,name="أحمد منصور",desc="أحمد منصور هو واحد من أكثر القادة تأثيرا شمال أفريقيا في التاريخ، لديه تأثير عميق على مستقبل التنمية في المغرب. القرن ال16 الميلادي، أصبحت أحمد منصور السعدي سلالة السلطان السادس، الذي، مع نظيره ماكرة الدبلوماسية الطرافة والمشاهير. وتحت نفوذه، فقد أصبح المغرب مهم في الساحة العالمية عصر النهضة."},
  [10018] = {id=10018,name="ماريا I",desc="في القرون القليلة الماضية من المأساة والجنون تتميز سلالة، ماريا I، فإنه مما لا شك واحدة من العاهل معروف في جميع أنحاء العالم، لكنها من المرجح أن يكون الملك المحبوب من الشعب البرتغالي أيضا. كما البرتغال - البرازيل - الغارف ملكة المملكة المتحدة، وقالت انها كانت تعرف باسم 'ماريا تقي'، وذلك لتجنب استدعائه هرب 'مجنون مريم' العدوان إلى البرازيل عندما كان نابليون معروفة في ذلك الوقت من الحكم البرتغالي. ماريا، بسبب المرض العقلي للتعذيب، توفي عام 1816 في ريو دي جانيرو."},
  [10019] = {id=10019,name="أودا نوبوناغا",desc="كان أمير الحرب الياباني أودا نوبوناغا قرن ال16. وقال انه ليس فقط جنرالا بارزا، وهو أيضا سياسي والدهاء، ولكن أيضا تبنته التكنولوجيا الجديدة - استبعد نوبوناغا ما يقرب من نصف في اليابان الاقطاعية بمختلف الوسائل. اثنين من رجاله - تويوتومي هيديوشي وتوكوغاوا إياسو - بعد الانتهاء من وفاته بأمر له، لأول مرة استبعاده تماما اليابان لأكثر من قرن من الزمان."},
  [10020] = {id=10020,name="غوستاف أدولفوس",desc="الملك السويدي رهيبة والقائد العسكري غوستافوس أدولفوس، أن 'الشمالية الأسد' المعروفة، البالغ من العمر 17 عاما بعد اعتلائه العرش مع الإصلاحيين الهوية رائع من فنون الحرب والاستراتيجية العسكرية أنشئت بسرعة سلطته. بعد رثت معقدة المحلي بعض الشباك الصراعات الخارجية من والده، كارل التاسع، حيث أدولف لا نضيع الوقت في تحالف الأرستقراطية معادية الماضي، وتدمير الاعداء السويد ولقيادة بلاده إلى القوى الأوروبية في القرن ال17 كما أنها تحتل المرتبة."},
  [10021] = {id=10021,name="إنريكو داندولو",desc="البندقية أربعين روندو حكم إنريكو داندولو واحترامها للتقوى، وطول العمر، ولكن أيضا لأنه يلعب دورا رئيسيا في الحملة الصليبية الرابعة، مما أدى إلى سقوط القسطنطينية الإمبراطورية البيزنطية وسيئة السمعة. وحقيقة أنه هو في الواقع رجل أعمى، لتجعل منه المزيد من الاهتمام في التاريخ. كاتدرائية صوفيا."},
  [10022] = {id=10022,name="إيزابيلا I",desc="إيزابيلا كانت ملكة قشتالة وليون، وحكم ثلاثة عقود، وقالت انها وزوجها فرديناند وضعت الأساس لإنشاء اسبانيا موحد. واحدة من مساهمتها في عملية توحيد العاهل في إسبانيا، التي أشرفت على الرحلة من كولومبوس في الأمريكتين، فضلا عن الانتهاء من توحيد النهائي من شبه الجزيرة الايبيرية 'حرب التحرير' (الاسترداد) وعنوان التاريخ الإسبانية الأكثر تأثيرا والأكثر الحبيب ."},
  [10023] = {id=10023,name="الإسكندر الأكبر",desc="الكسندر المقدوني هو بلا شك واحدة من أعظم الجنرالات التاريخ البشري. في سنوات فقط 17، قاد جيشه في جميع أنحاء أوروبا وآسيا وأفريقيا والشرق الأوسط، غزا كل ون مينغ يتمكن من الوصول إليها."},
  [10024] = {id=10024,name="المهاتما غاندي",desc="المهاتما غاندي كان موهندس غاندي الهندي وطنيا فعالا في أوائل القرن 20 إلى المدى المتوسط، قاد الشعب الهندي ضد حكم الإمبريالية البريطانية ونالت استقلالها عن الهند. كان رائدا في 'العصيان المدني' عقيدة، يدعو الوسائل السلمية للنضال ضد الاستبداد، والدوران ملحوظا في النضال ضد الحكم الاستعماري البريطاني في."},
  [10025] = {id=10025,name="إليزابيث الأولى",desc="إليزابيث الأولى ولدت في عصر المتميز لامرأة المعلقة. جميلة، والحكمة، قوية للغاية في الأوقات والمواقف الصعبة في بلدها ليس فقط من أجل البقاء، لا تزال المرأة الوحيدة التي يتم التعامل معها على أنها عصر التحركات الملكية حكم بلد كبير."},
  [10026] = {id=10026,name="وو",desc="مثل معظم الحضارة في التاريخ الصيني وعادة ما يهيمن عليها الرجال. فترة طويلة جدا من الزمن، تكون قادرة على التمتع بحقوق امرأة صغيرة جدا، يمكنك السيطرة على العالم يكاد يكون من المستحيل. يمكن للمرأة أن تسلق الإمبراطور كله من موقف الصين أقوى، هو ببساطة غير مسموع. على مر التاريخ الصيني، هناك امرأة واحدة فقط تفعل ذلك. وهي الإمبراطورة وو، أعظم حاكم في العالم واحد (ذكر أو أنثى)."},
  [10027] = {id=10027,name="هارالد هاردرادا",desc="هارالد - في وقت لاحق دعا رعاياه له برنارد رادا (يفترض أن 'حاكم شديد اللهجة' يعني) - هو ملك النرويج لا يشبع، وقال انه حاول في 1048 في الملك الدنماركي (في وقت لاحق أيضا حاولت عدة مرات)، وأيضا في عام 1066 عندما ملك انكلترا في محاولة. انه يريد ان يكون ملك انكلترا هذه الرغبة في قتله. قبل وفاته في ستامفورد بريدج، هارالد برنارد رادا التوسع في الواقع، رحلة له إلى بلد بعيد ... وكان الناس هناك قتلوا جميعا."},
  [10028] = {id=10028,name="تشين شي هوانغ",desc="تشين شي هوانغ أولا في الصين لاستكمال توحيد شخصية سياسية البلاد بقبضة حديدية، والمعروف أيضا باسم إمبراطور القديم والحديث العاهل الأول."},
  [10029] = {id=10029,name="ثيودور روزفلت",desc="ثيودور روزفلت في التاريخ الأميركي، وأصغرهم سنا في الرئيس. شخصيته الفريدة والسياسات الإصلاحية، مما يجعل منه واحدا من أعظم الرؤساء في تاريخ الولايات المتحدة."},
  [10030] = {id=10030,name="صلاح الدين الأيوبي",desc="صلاح الدين الأيوبي الاستراتيجيين العالم الإسلامي في القرون الوسطى الشهير العسكري، ورجل دولة، وهو أول سلطان سلالة مصر الأيوبية."},
  [10031] = {id=10031,name="بيدرو الأول",desc="بيدرو الأول (بيدرو الأول، 1798 نيان 10 12-24 سبتمبر 1834)، ولدت في لشبونة، البرتغال، اول امبراطور للبرازيل (12 أكتوبر 1822 - في أي 7 أبريل 1831) وقال الأب المؤسس بيدرو الأول 1826 10 مارس - 28 مايو بدوام جزئي ملك البرتغال، قال بيدرو الرابع، عاد إلى أوروبا في عام 1831، توفي من المرض في عام 1834 في البرتغال. الملقب ب 'جندي الملك' ، 'الإمبراطور الملك' أو 'المحررين'"},
  [10032] = {id=10032,name="كليوباترا",desc="المعروفة باسم كليوباترا. بطليموس سلالة من الماضي فرعون مصر القديمة الإناث. أنها تسمح أفعى سامة لانهاء وقت واحد حياتهم ومصر (ولكن الأبحاث أثبتت أن توفيت إمكانية أوكتافيان قتل أكبر). ومنذ ذلك الحين، أصبحت مصر جزءا من الإمبراطورية الرومانية حتى زوال الإمبراطورية الرومانية الغربية في القرن الخامس."},
  [10033] = {id=10033,name="الملكة فيكتوريا",desc="الملكة فيكتوريا ثاني أطول فترة حكم الملك، بعد الملكة اليزابيث الثانية. استمر حكم 64 عاما. وهي أيضا أول مع 'ملكة بريطانيا العظمى وأيرلندا، إمبراطورة على الهند والبلاد' على غرار اسم-ملكة انكلترا. خلال فترة حكمها (1837 السنة - 1901) هو الأقوى المملكة المتحدة، 'الشمس لا تغرب الإمبراطورية' الفترة."},
  [10034] = {id=10034,name="لويس الرابع عشر",desc="لويس الرابع عشر ملك بوربون فرنسا وملك نافار، وحكم لمدة تصل إلى 72 عاما 18 مارس يوما، هي واحدة من العاهل أطول فترة حكم، بل هو أيضا سجل دقيق لأطول فترة حكم ملك السيادية في تاريخ أوروبا ."},
  [10035] = {id=10035,name="فريدريك I",desc="فريدريك I المعروفة باسم 'اللحية الحمراء' أو 'بارباروسا' (بارباروسا)، وأوروبا في العصور الوسطى الإمبراطور الروماني المقدس، هو رجل دولة الألمانية التاريخية، الاستراتيجي العسكري. فريدريك ذلك، يعتبر واحدا من الحكام الأكثر نجاحا في ألمانيا في العصور الوسطى، خلال فترة رئاسته، وصلت القوة الوطنية الإمبراطورية الرومانية المقدسة ذروتها، لتصبح أقوى دولة في أوروبا كلها."},
  [10036] = {id=10036,name="توكوغاوا",desc="توكوغاوا، في اليابان، فترة الدول المتحاربة في وقت متأخر، فترة أزوتشي موموياما اسم مقاطعة ميكاوا، ايدو شوغن فرضت أول مرة من قبل شوغون. الدول المتحاربة اليابان ثلاثة أفيفا (والاثنان الآخران أودا نوبوناغا، تويوتومي هيديوشي) واحد. دولة بارز والاستراتيجي في التاريخ الياباني."},
  [10037] = {id=10037,name="أشوكا",desc="الهندية القديمة مملكة ماجادها ماوريان الجيل الثالث للملك، شعبية أشوكا بين الإمبراطور الهندي القديم لا مثيل لها، له تأثير على التاريخ ويمكن أيضا المرتبة اول امبراطور الهند القديمة."},
  [10038] = {id=10038,name="كوبلاي خان",desc="كوبلاي خان المنغولية، ورجل دولة، الاستراتيجي العسكري. الإشراف على ابن البلد توري الرابع، أسرة يوان مونتغمري شقيق. خان الأخيرة من الإمبراطورية المغولية، ولكن أيضا الإمبراطور المؤسس لأسرة يوان. منغوليا تسمية 'شوا تشان خان'."},
  [10039] = {id=10039,name="بيتر I",desc="بيتر I القيصر الروسي رومانوف سلالة الجيل الرابع (1682─1725)، الامبراطور الروسي (1721 --1725 سنوات). اعتلى العرش عام 1682، شنت السلطة الحقيقية في 1689 الإصلاحات. باعتبارها واحدة من 'العظمى' اثنين فقط من سلالة رومانوف، في عهد روسيا لتنفيذ الإصلاحات السياسية والاقتصادية والعسكرية، وقال انه وضعت السياسة الغربية هو جعل روسيا إلى قوة من العوامل الرئيسية. يعتبر بطرس الأكبر عموما أن يكون الأباطرة الأكثر تميزا في روسيا."},
  [10040] = {id=10040,name="مونتيزوما I",desc="مونتيزوما I كان محارب قوي وزعيم، وقال انه دفع الأمة ازتيك عظيم ومجيد، وليس للذي قد شاهدت بلا حول ولا قوة كما يتم تقسيم إمبراطوريته عليها في الغزاة الأسبان مع حفيده مونتيزوما، والخلط."},
  [10041] = {id=10041,name="كارميلو ها",desc="كارميلو ها هي أبرز القادة المرموقين في تاريخ شيا وى يى، كان أول من توحيد الجزر شيا وى يى كمملكة مستقلة الرئيسية شيا وى يى الرجل العظيم. ولادته وحياته المبكرة ترتبط دائما مع النبوءة القديمة الأصلية شيا وى يى التاريخ شيا وى يى وتصبح في نهاية المطاف أعظم المحاربين. بالإضافة إلى قوة عسكرية قوية، وبطاقة الولايات المتحدة أو رجل دولة عظيم، وقد وضعت في زمن الحرب مرسوم المهم، والذي أصبح السلف للقانون الإنساني العديد من العالم."},
  [10042] = {id=10042,name="زائد ريتشارد مدى",desc="زائد ريتشارد مدى يعني الفيل العام باعتباره رمزا للوطنية وبطلا قوميا في إندونيسيا، في القرن ال14 من السنوات التي قضاها رئيسا للوزراء هذا هو تاريخ الفترة أقوى في إندونيسيا. عن طفولته ونشأته الاحتفاظ بالسجلات قليلا، إلا أنه ولد في أسرة مدنية، والاعتماد على المضيف الماكرة الطرافة ولا يرحم في ساحة المعركة. كملك الحراس، وقال انه ساعد في قمع التمرد، ثم إلى الطبقة الحاكمة، استقل النهائي رئيس الوزراء العرش."}
}
return DLeader