  [224] = {id=224,title="ميكانيكيات القاذفة القنابل",desc="ميكانيكيات القاذفة القنابل ستزيد خصائص الهجوم والدفاع والطاقة للقاذفة القنابل",content="1، سيستهلك الفولاذ وموارد الميكانيكيات لترقية ميكانيكيات القاذفة القنابل. 2، يمكنك الحصول على الفولاذ من محطة المعارك. 3، موارد الميكانيكيات انتاج من مصنع الميكانيكي أو يمكنك شراءها من لاعبين آخرين في تجارة ميكانيكية."},
  [225] = {id=225,title="مستوى القائد",desc="بعد ترقية مستوى القائد يمكنك الحصول على فائدة تقوية الخاصية ومهارات خاصة",content="1، يمكنك الحصول على نقاط القائد بعد الهجوم في محطة المعارك. 2، يمكنك الحصول على نقاط القائد بعد قضاء على قرصان المجرة. 3، يمكنك الحصول على نقاط القائد من عجلة المجرة أو مهمات يومية أو فعاليات."},
  [226] = {id=226,title="مستوى العصر",desc="بعد ترقية مستوى العصر يمكنك زيادة العد الأقصى لأسطول واحد",content="1، يمكنك الحصول على نقاط الازدهار التي تستخدمها لترقية الحضارة."},
  [227] = {id=227,title="مستوى رئيس الأركان",desc="بعد ترقية مستوى رئيس الأركان يمكنك زيادة العد الأقصى لأسطول واحد",content="1، سيستهلك أمر التقيب لترقية مستوى رئيس الأركان، ويمكنك الحصول على أمر التقيب بعد قضاء على قرصان المجرة. 2، يمكنك شراء كثير من أمر التقيب باستخدام الماس."},
  [228] = {id=228,title="مستوى الرتبة العسكرية",desc="بعد ترقية مستوى رئيس الأركان يمكنك زيادة العد الأقصى لأسطول واحد وفائدة تقوية الخاصية",content="1، يمكنك الحصول على نقاط قيمة الجيش بعد تدمير سفن العدو. كلما كان مستوى السفن أعلى كلما حصلت على النقاط بعدد أكبر. 2، اذا وصل عدد نقاط قيمة الجيش الى الحد المعين سيقوم بترقية مستوى الرتبة العسكري."},
  [229] = {id=229,title="مستوى VIP",desc="بعد تفعيل VIP وترقيته، يمكنك الحصول على فائدة تقوية الخاصية وامتيازات مختلفة",content="1، يجب عليك أن تحصل على نقاط VIP لكي ترقي مستوى VIP. يمكنك الحصول على النقاط من مهمات يومية أو معارك أو قرصان المجرة. 2، يمكنك استخدام الألماس لشراء مزيد من النقاط مباشرة. 3، ستفعل امتيازات VIP بعد تفعيله."},
  [230] = {id=230,title="قوة الفرغاطة البارجة",desc="تكون الفرغاطة البارجة ضد فعالة العترضة وحاملة الطائرات من العدو",content="1، يمكنك صنع الفرغاطة البارجة في مصنع السفن الحربية، ويمكنك صنعها أسرع باستخدام الماس. 2، بعد ترقية مصنع الفرغاطة البارجة يمكنك ترقية مستوى الفرغاطة البارجة."},
  [231] = {id=231,title="قوة العترضة",desc="تكون العترضة ضد فعالة مطوفة السفينة وحاملة الطائرات من العدو",content="1، يمكنك صنع العترضة في مصنع السفن الحربية، ويمكنك صنعها أسرع باستخدام الماس. 2، بعد ترقية مصنع العترضة يمكنك ترقية مستوى العترضة."},
  [232] = {id=232,title="قوة مطوفة السفينة",desc="تكون مطوفة السفينة ضد فعالة الفرغاطة البارجة وحاملة الطائرات من العدو",content="1، يمكنك صنع مطوفة السفينة في مصنع السفن الحربية، ويمكنك صنعها أسرع باستخدام الماس. 3، بعد ترقية مصنع مطوفة السفينة يمكنك ترقية مستوى مطوفة السفينة."},
  [233] = {id=233,title="قوة حاملة الطائرات",desc="تكون حاملة الطائرات ضد فعالة المعدات الدفاعية لدي عدو",content="1، يمكنك صنع حاملة الطائرات في مصنع السفن الحربية، ويمكنك صنعها أسرع باستخدام الماس. 4، بعد ترقية مصنع حاملة الطائرات يمكنك ترقية مستوى حاملة الطائرات."},
  [234] = {id=234,title="قوة الدفاع",desc="تكون معدات دفاعية ضد فعالة الفرغاطة البارجة والعترضة ومطوفة السفينة من العدو",content="1، يمكنك صنع الأسلحة الدفاعية في مركز المعارك وصنعها أسرع باستخدام الماس. 2، بعد ترقية مركز المعارك يمكنك ترقية مستوى الأسلحة الدفاعية."},
  [235] = {id=235,title="تطوير التكنولوجيا",desc="بعد تطوير التكنولوجيا سيقلل وقت ترقية المبانى ووقت صيانة السفن",content="1، سيستهلك عامل التكنولوجيا و4 أنواع من الموارد الأساسية لدراسة تطوير التكنولوجيا. 2، يمكنك الحصول على عامل التكنولوجيا بعد قضاء على قرصان المجرة أو اكمال المهمات اليومية. 3، يمكنك دراسة تطوير التكنولوجيا أسرع باستخدام الماس."},
  [236] = {id=236,title="تكنولوجيا عسكري",desc="تكنولوجيا عسكري سيزيد نقاط الخصائص المختلفة لـ4 أنواع من السفن",content="1، سيستهلك عامل التكنولوجيا و4 أنواع من الموارد الأساسية لدراسة التكنولوجيا العسكري. 2، يمكنك الحصول على عامل التكنولوجيا بعد قضاء على قرصان المجرة أو اكمال المهمات اليومية. 3، يمكنك دراسة التكنولوجيا العسكري أسرع باستخدام الماس."},
