  [458] = {id=458,title="قوة الدفاع",desc="تكون معدات دفاعية ضد فعالة الفرغاطة البارجة والعترضة ومطوفة السفينة من العدو",content="1، يمكنك صنع الأسلحة الدفاعية في مركز المعارك وصنعها أسرع باستخدام الماس. 2، بعد ترقية مركز المعارك يمكنك ترقية مستوى الأسلحة الدفاعية."},
  [459] = {id=459,title="تطوير التكنولوجيا",desc="بعد تطوير التكنولوجيا سيقلل وقت ترقية المبانى ووقت صيانة السفن",content="1، سيستهلك عامل التكنولوجيا و4 أنواع من الموارد الأساسية لدراسة تطوير التكنولوجيا. 2، يمكنك الحصول على عامل التكنولوجيا بعد قضاء على قرصان المجرة أو اكمال المهمات اليومية. 3، يمكنك دراسة تطوير التكنولوجيا أسرع باستخدام الماس."},
