-- L_领袖数据配置文字表.xlsx
-- id=英雄ID,name=英雄名称,desc=英雄介绍,
local DLeader = {
  [10001] = {id=10001,name="哈伦·拉希德",desc="哈伦·拉希德是阿拔斯朝的第五任哈里发，他于786到809年间统治着阿拉伯帝国。在他的任期中阿拔斯朝的领土向西扩张到了西班牙，向北直达安纳托利亚，往东则直抵印度。并且他是那时世界上最为强大和繁荣的政治实体。哈伦是个非常有才干的领导人，他的统治时期见证了科技和文化的发展昌盛。"},
  [10002] = {id=10002,name="拉美西斯二世",desc="拉美西斯二世是埃及最伟大、最强大的法老。他在20岁时登位，统治时间超过60年。拉美西斯也是一位伟大的军事领袖和杰出的建设者。他建造了辉煌的新首都拉美西斯城。一些历史学家还认为拉美西斯就是圣经故事中摩西出埃及时期的法老。"},
  [10003] = {id=10003,name="海尔·塞拉西",desc="被誉为“犹太族之斗狮”、“万王之王”以及“上帝的特使”的海尔·塞拉西在20世纪统治埃塞俄比亚将近四十年。1892年出生的里·塔法里·马康南，将成为后世的海尔·塞拉西，他一生都在为维护埃塞俄比亚的利益和将其带向非洲政权之首而努力。海尔·塞拉西以努力推进埃塞俄比亚的现代化，改革落后封建世俗而闻名，他还孜孜不倦地为埃塞俄比亚在国际舞台上能获得一席之地而奋斗。"},
  [10004] = {id=10004,name="玛丽亚·特蕾西亚",desc="在18世纪，神圣罗马帝国和强大的奥地利哈布斯堡王朝都是由勇敢而坚强的玛丽亚·特蕾西亚所领导。她与那些反对她统治的欧洲势力作战，并勇敢地保护了她的臣民。在哈布斯堡家族近650年的统治中，玛丽亚·特蕾西亚是第一位也是唯一一位女性统治者，而且她也认为是家族历史中最成功的统治者之一。"},
  [10005] = {id=10005,name="苏莱曼一世",desc="被人们尊称为“大帝”、“立法者”或“大土耳其之君”，是伊斯兰教的哈里发和奥斯曼土耳其帝国的苏丹。他的统治从1520年继位起直至1566年逝世。在他的统治期间，苏莱曼极大地拓展了帝国的疆域，欧亚非和中东的统治者们对他即害怕又崇敬。"},
  [10006] = {id=10006,name="佩德罗二世",desc="佩德罗二世是首位出生在巴西的巴西君主，他在位期间给国家带来了稳定与发展，并使其成为南美洲的强盛国家。他是个沉着严肃的男子，他自认是国家政治生命的裁决者，他运用君主立宪的大权，击退试图控制巴西的强大政治团体。他领导巴西三场战争，在获得了新的领土和声誉的同时，他也努力集中精力把巴西建设成一个现代化国家。他鼓励生产咖啡，而不是糖。并且积极支持和赞助公路，铁路，电报和海底电缆的建设。"},
  [10007] = {id=10007,name="卡西米尔三世",desc="波兰国王卡西米尔三世，是波兰历史上唯一赢得了“大帝”称号的君主。他是一位果断又有才干的统治者，依靠他的外交才能和明智的决策统治着14世纪的波兰，并让他的国家变得更为富裕和强大。恢复经济，强化军事，编纂法典，并扩展波兰领土，卡西米尔让他的国家变得更加富强——也让他成为了波兰历史上最伟大的君主之一。"},
  [10008] = {id=10008,name="李裪",desc="人称“世宗大王”的李裪，是朝鲜王朝的第四任国王，从1418年开始统治朝鲜直到1450年逝世。李裪被许多人认为是朝鲜半岛历史上最伟大的国王，以其关怀和尊重民生而备受赞誉，其任内为人民的福祉而推行了许多民事和社会政策。世宗还鼓励科学技术的发展，并创制了朝鲜自己的文字系统“训民正音”。"},
  [10009] = {id=10009,name="哈拉德·戈尔姆森",desc="“蓝牙”哈拉德·戈尔姆森是约从公元958年开始统治丹麦三十年之久的国王。在位期间，蓝牙统一了丹麦各部，并击退了挪威人和日耳曼人的入侵，还下令建造了许多要塞以加强国防。蓝牙还因抛弃传统的北欧原始信仰，努力引导国民皈依基督教而闻名于世。"},
  [10010] = {id=10010,name="奥托·冯·俾斯麦",desc="人称“铁血宰相”的奥托·冯·俾斯麦也许是德国历史上最重要的人物。在他长期的政治生涯中，俾斯麦完成了德国的统一，建立了德意志帝国。在他30年的首相任期中，德国从一个弱小而松散的王国联盟转型为一个强大的统一国家，并在中欧占据绝对统治地位。"},
  [10011] = {id=10011,name="叶卡捷琳娜",desc="叶卡捷琳娜大帝在18时期的后期统治俄国。她见证了俄罗斯帝国的迅速扩张，用军事力量和高明的外交手段给俄国增加了数万平方英里地盘。她是个美丽又聪慧的女子，她运用自己的魅力迷倒了欧洲最聪明的人，使她的宫廷成为整个欧洲新思想的中心。虽然她出生于德国，叶卡捷琳娜大帝却是俄国历史上最伟大的君主之一。"},
  [10012] = {id=10012,name="拿破仑·波拿巴",desc="拿破仑·波拿巴生于科西嘉岛，十岁时进入当地军事学校，在数学，历史和地理上极具天赋，对文学和人文学科则完全漠不关心。14岁那年他被委任为一个炮兵团的少尉。当法国革命爆发时，拿破仑站在革命派一边并升任炮兵中校，他迅速建立起了能征善战的名望。"},
  [10013] = {id=10013,name="威廉·奥兰治",desc="公认的荷兰国家建立者和解放者威廉·奥兰治，人称“沉默者威廉”，他在公元16世纪中期领导尼德兰诸省人民发起反抗西班牙哈布斯堡王朝的统治的起义并获得胜利。威廉原本是西班牙国王腓力二世的忠实臣属，但西班牙王室对尼德兰诸省新教徒的不断迫害激起了他的极度义愤，最终成为独立起义最重要的领袖。威廉大力召集其他贵族加入反抗事业，成为西班牙王室眼中的叛贼，他率领荷兰军队在战场上赢得多场胜利，然而在1584年死于刺客的一枚子弹。"},
  [10014] = {id=10014,name="盖乌斯·屋大维",desc="奥古斯都原名盖乌斯·屋大维，他是第一位罗马皇帝（也可以说是最伟大的一位）。他结束了持续一个世纪的内战，并开创了持续两百多年的“太平盛世”——罗马统治下的和平，这是罗马文化和艺术极度繁荣的黄金时代。"},
  [10015] = {id=10015,name="华盛顿",desc="华盛顿是18世纪晚期，殖民地时期美国的诸多伟人中的一员。虽然他没有约翰·亚当斯那样善战、本杰明·富兰克林那样创意十足，或者托马斯·杰弗逊那样聪慧，但华盛顿也拥有独特的个人魅力——那就是不论在战争时期还是和平时期的领导能力。他带领着大陆军在极端不利的情况下赢得了一场不可思议的胜利，也由此为他的国家带来了独立。"},
  [10016] = {id=10016,name="成吉思汗",desc="铁木真（成吉思汗）是人类历史上最著名和最令人畏惧的领袖之一。数以百计的塑像、建筑和商品上都有他的形象，无数的娱乐产品描述着他的事迹。他将亚洲北方高原上纷争不断的游牧部落团结为一体，建立起有史以来最广阔的陆上帝国。他少为人知的成就则是在制度设计、商业贸易和宗教自由等领域。他不但是一位军事统帅，还是一位机敏的政治领袖。"},
  [10017] = {id=10017,name="艾哈迈德·曼苏尔",desc="艾哈迈德·曼苏尔是北非历史上最有影响力的领袖之一，他对摩洛哥的未来发展有着极其深远的影响。公元16世纪，艾哈迈德·曼苏尔成为了萨阿迪王朝的第六任苏丹，他以其机智狡猾的外交手腕而闻名于世。正是在他的影响下，摩洛哥成为了文艺复兴时期世界舞台上的重要一员。"},
  [10018] = {id=10018,name="玛丽亚一世",desc="在这个持续数世纪的以悲剧和癫狂为特点的王朝中，玛丽亚一世无疑是其中一位为世人所知的君主，同时她还很有可能是最受葡萄牙人民爱戴的君主。作为葡萄牙－巴西－阿尔加维联合王国女王，她因在统治葡萄牙时被称为“虔诚的玛丽亚”和为了躲避拿破仑侵略逃至巴西时被称为“疯狂的玛丽亚”而为人所熟知。玛丽亚一世因受精神疾病折磨，1816年驾崩于里约热内卢。"},
  [10019] = {id=10019,name="织田信长",desc="织田信长是一位16世纪的日本军阀。他不仅是一位杰出的将军，还是一位机智的政治家，也是一位新科技的接纳者——信长用各种手段统治近半个封建时期的日本。他的两位部下——丰臣秀吉和德川家康——在他死后完成了他的遗志，第一次完全统治了日本超过一个世纪之久。"},
  [10020] = {id=10020,name="古斯塔夫·阿道夫",desc="令人敬畏的瑞典国王和军事指挥官古斯塔夫·阿道夫，即以“北方的雄狮”闻名，在17岁即位之后便以高超的军事策略和战争艺术方面的改革家的身份迅速建立了自己的权威。从他的父亲卡尔九世那里继承了复杂的国内有国外冲突网之后，阿道夫没有浪费任何时间就与过去的敌对贵族结盟，摧毁了瑞典的敌人，并带领他的国家在17世纪进入了欧洲强国的行列。"},
  [10021] = {id=10021,name="恩里科·丹多洛",desc="威尼斯的第四十二任督治恩里科·丹多洛，以其虔诚和长寿受人尊敬，但也因为在第四次十字军东征中扮演关键角色，导致君士坦丁堡陷落与拜占庭帝国灭亡而声名狼藉。而他自己实际上是一个瞎子的事实，使他在历史中更为瞩目。索菲亚大教堂。"},
  [10022] = {id=10022,name="伊莎贝拉一世",desc="伊莎贝拉一世是卡斯提尔和莱昂女王，在位三十年，她与她的丈夫斐迪南一道为统一西班牙的建立奠定了基础。她以对西班牙统一进程的贡献，对哥伦布远航美洲的赞助以及最终完成统一伊比利亚半岛“光复战争”（Reconquista）而被尊为西班牙历史上最具影响力和最受人爱戴的君主之一。"},
  [10023] = {id=10023,name="亚历山大大帝",desc="马其顿的亚历山大毫无疑问是人类历史上最伟大的将领之一。在短短的17年间，他率领他的军队横扫欧洲、亚洲、非洲以及中东地区，征服了他所能触及的所有文明。"},
  [10024] = {id=10024,name="莫罕达斯·甘地",desc="莫罕达斯·甘地是活跃在20世纪初期到中期的一位印度爱国者，他领导印度人民反抗英国帝国主义对印度的统治并赢得独立。他首创了“非暴力不合作”主义，主张采取和平抗争的手段来反对暴政，在反抗英国殖民统治的斗争中成交卓著。"},
  [10025] = {id=10025,name="伊丽莎白一世",desc="伊丽莎白是出生于一个杰出的时代的一位杰出的女人。美丽、睿智、坚强无比的她在艰难的时局中不但生存了下来，还在那个女人仅被当作会移动的财产的时代统治着一个大国。"},
  [10026] = {id=10026,name="武则天",desc="和多数文明一样，中国的历史通常是由男人主宰的。很长的一段时间内，女人能享有的权利非常少，能掌握天下的更是几乎不可能的。女人能爬上皇帝这个全中国最有权势的地位，简直是闻所未闻。纵观整个中国历史，只有一个女人做到了这一点。她就是武则天，这个世界上最伟大的统治者（无论男女）之一。"},
  [10027] = {id=10027,name="无情者哈拉尔",desc="哈拉尔德——后来他的臣民称他为哈德拉达（大概是“严厉的统治者”的意思）——是位永不满足的挪威国王，他曾试图于1048年在丹麦称王（后来还尝试过几次），还在1066年尝试当英国国王。他想成为英国国王这一欲望害死了他。在他死于斯坦福桥之前，哈拉尔德·哈德拉达确实在不断扩张，他长途跋涉前往遥远的国家…并将那里的人统统杀死。"},
  [10028] = {id=10028,name="秦始皇",desc="中国历史上著名的政治家、战略家、改革家，首位完成华夏大一统的铁腕政治人物，也是古今中外第一个称皇帝的君主。"},
  [10029] = {id=10029,name="西奥多·罗斯福",desc="美国历史上最年轻的在任总统。他的独特个性和改革主义政策，使他成为美国历史上最伟大的总统之一。"},
  [10030] = {id=10030,name="萨拉丁",desc="中世纪穆斯林世界著名军事家、政治家，埃及阿尤布王朝首任苏丹。"},
  [10031] = {id=10031,name="佩德罗一世",desc="佩德罗一世（Pedro I，1798年10月12日－1834年9月24日），出生于葡萄牙里斯本，巴西的第一任皇帝（1822年10月12日—1831年4月7日在任）、国父，称佩德罗一世，1826年3月10日至5月28日兼任葡萄牙国王，称佩德罗四世，1831年返回欧洲，1834年因病去世于葡萄牙。绰号“士兵国王”（O Rei-Soldado）、“皇帝国王”（O Rei-Imperador）或“解放者”（O Libertador ）"},
  [10032] = {id=10032,name="克莉奥佩特拉",desc="通称为埃及艳后。是古埃及的托勒密王朝最后一任女法老。她让一条毒蛇咬死自己来同时结束自己和埃及的生命（不过，研究却证明她死于屋大维谋杀的可能性更大些）。从此以后，埃及成为了罗马帝国的一部分，直到5世纪西罗马帝国的灭亡。"},
  [10033] = {id=10033,name="维多利亚女王",desc="英国历史上在位时间第二长的君主，仅次于伊丽莎白二世女王。在位时间长达64年。她也是第一个以“大不列颠和爱尔兰国女王和印度女皇”名号称呼的英国女王。她在位的期间（1837年―1901年）是英国最强的“日不落帝国”时期。"},
  [10034] = {id=10034,name="路易十四",desc="波旁王朝的法国国王和纳瓦拉国王，在位长达72年3月18天，是在位时间最长的君主之一，也是有确切记录在欧洲历史中在位最久的独立主权君主。"},
  [10035] = {id=10035,name="腓特烈一世",desc="被称为“红胡子”或“巴巴罗萨”（Barbarossa），是欧洲中世纪神圣罗马帝国的皇帝，也是德意志历史上著名的政治家、军事家。腓特烈一世被认为是中世纪德国最成功的统治者之一，在其执政期间，神圣罗马帝国的国力达到顶峰，成为全欧最强的国家。"},
  [10036] = {id=10036,name="德川家康",desc="日本战国时代末期、安土桃山时代三河国大名，江户幕府第一代征夷大将军。日本战国三英杰（另外两位是织田信长，丰臣秀吉）之一。日本历史上杰出的政治家和军事家。"},
  [10037] = {id=10037,name="阿育王",desc="古代印度摩揭陀国孔雀王朝的第三代国王，阿育王的知名度在古印度帝王之中是无与伦比的，他对历史的影响同样也可居古印度帝王之首。"},
  [10038] = {id=10038,name="孛儿只斤·忽必烈",desc="蒙古族，政治家、军事家。监国托雷第四子，元宪宗蒙哥弟。大蒙古国的末代可汗，同时也是元朝的开国皇帝。蒙古尊号“薛禅汗”。"},
  [10039] = {id=10039,name="彼得一世",desc="俄罗斯罗曼诺夫王朝第四代沙皇（1682─1725）、俄罗斯帝国皇帝（1721年—1725年）。1682年即位，1689年掌握实权发动改革。作为罗曼诺夫朝仅有的两位“大帝”之一，在位期间对俄国推行的政治、经济和军事等改革，他制定的西方化政策是使俄罗斯变成一个强国的主要因素。彼得大帝一般被认为是俄罗斯最杰出的皇帝。"},
  [10040] = {id=10040,name="蒙特祖玛一世",desc="蒙特祖玛一世是一位强大的武士及领袖，他将阿兹特克民族推向了伟大和光辉，不要把他跟他那位曾无助地看着自己的帝国被西班牙征服者分解的孙子，蒙特祖玛二世所弄混淆。"},
  [10041] = {id=10041,name="卡美哈梅哈",desc="卡美哈梅哈是夏威夷历史上最受人尊敬的领袖，是首位将夏威夷各主要岛屿统一为独立的夏威夷王国的伟人。他的出生和早年生活总是同夏威夷土著古老的预言联系在一起，并最终成为夏威夷历史上最伟大的战士。除了强大的军事力量，卡美哈梅哈还是一位伟大的政治家，他制定了重要的战时法令，这成为世界上许多人道主义法律的前身。"},
  [10042] = {id=10042,name="加查·玛达",desc="“加查·玛达”意为大象将军，作为印尼民族英雄和爱国主义的象征，14世纪时他作为首相的这段岁月是印尼历史上最强大的时期。有关他幼年和成长经历的记录存留很少，只知道他出生于平民家庭，靠着机智狡猾上位，并且在战场上心狠手辣。作为一个国王侍卫，他帮助镇压了叛乱，从此步入了统治阶层，最终登上首相宝座。"}
}
return DLeader