  [3] = {id=3,title="统帅等级",desc="提升统帅等级可提升战舰单支编队上限",content="1.升级统帅等级需要消耗统帅令，击杀星际海盗可获得统帅令。\n2.花费钻石可快速购买大量的统帅令。"},
  [4] = {id=4,title="军衔等级",desc="提升军衔等级可提升战舰单支编队上限，还可获得增益属性",content="1.击杀其他玩家的战舰即可获得军功值，击杀战舰等级越高，获得军功值越多。\n2.军功值达到军衔要求时可提升军衔等级。"},
  [5] = {id=5,title="VIP等级",desc="提升并激活VIP，可获得强大增益属性和各种特权",content="1.升级VIP等级需要获得VIP点数，日常任务、战役、星际海盗都掉落VIP点数。\n2.花费钻石可快速购买大量的VIP点数。\n3.只有激活VIP后，VIP特权才会生效。"},
  [6] = {id=6,title="护卫舰战力",desc="护卫舰可有效对抗敌人的截击舰和空母舰",content="1.在战舰工厂可以打造护卫舰，消耗钻石可快速打造。\n2.升级护卫舰工厂可提升护卫舰的等级。"},
