-- L_领袖数据配置文字表.xlsx
-- id=英雄ID,name=英雄名称,desc=英雄介绍,
local DLeader = {
  [10001] = {id=10001,name="Harun al-Rashid",desc="Harun al-Rashid fue el quinto Califa Abasí, gobernando el Imperio Arabe desde 786 hasta 809 DC. Durante su reinado, el Califa presionó desde España al Oeste, Anatolia al Norte, e India al Este, y fue la más grande y poderosa entidad política en el mundo. Harun fue un gobernante capaz, y su reinado tuvo un tiempo de avance científico y cultural y próspero para sus súbditos."},
  [10002] = {id=10002,name="Ramses II",desc="Ramses II es considerado el más grande y poderoso faraón de Egipto. Tomando el trono a sus veintes, Ramses gobernó Egipto por más de 60 años. Ramses es recordado como un gran líder militar así como instituir programas de construcción extensivos. También es recordado por construir una nueva ciudad capital: Pi-Ramses. Algunos historiadores creen que Ramses el el faraón de la historia bíblica de Moisés."},
  [10003] = {id=10003,name="Haile Selassie",desc="Conocido como el 'León Conquistador de la Tribu de Judá', 'Rey de Reyes', y el 'Elegido de Dios', Haile Selassie reinó Etiopía como emperador por casi 40 años en el siglo 20. Nacido como Lij Tafari Makonnen en 1892, el hombre que llegó a ser conocido como Haile Selassie pasó su vida sirviendo a los intereses de Etiopía y llevó a su nación a la vanguardia en política Africana. Mejor conocido por reformar las antiguas prácticas feudales de Etiopía a través de sus intentos de modernizar la nación, Haile Selassie trabajó incansablemente para ganar reconocimiento internacional para Etiopía a nivel mundial."},
  [10004] = {id=10004,name="María Teresa",desc="Durante el siglo 18 el Sagrado Imperio Romano y la poderosa Monarquía Hasburga de Austria fueron dirigidos por la siempre persistente María Teresa, quien desafió los poderes Europeos que se oponían a su reinado y sirvió fervientemente a los objetivos de su reino. En casi 650 años del gobierno Hasburgo, María Teresa fue la primera y única mujer en liderar al imperio, y demostró estar entre los gobernantes más exitosos en su historia."},
  [10005] = {id=10005,name="Solimán I",desc="Solimán I, conocido como 'El Magnífico', 'El Legislador' y 'El Gran Turco', fue el Califa del Islam y el Sultán del Imperio Otomano, tomando los reinos Turco en 1520 y gobernándo hasta su muerte en el año 1566. Durante su mandato Solimón expandió enormemente los territorios del imperio, obteniendo el temor y admiración a regañadientes de los líderes de Europa, Africa, Asia y el Medio Este."},
  [10006] = {id=10006,name="Pedro II",desc="El primer monarca Brasileño nacido en Brasil. Durante su largo reinado Pedro II llevó estabilidad y progreso a su tierra, y la llevó a ser la fuerza preeminente de América del Sur. Una persona calmada y seria, Pedro II celosamente protegió la soberanía de su nación, desafiando al poderoso Imperio Británico y ahuyentando a los Estados Unidos cuando esos países buscaron entrometerse en la región. Dirigió a Brasil en tres guerras, obteniendo nuevos territorios y prestigio, mientras continuaba enfocado en hacer de Brasil un estado moderno. Estimuló la producción del café en lugar del azúcar, apoyando e invirtiendo activamente en caminos, ferrocarril, telégrafo y cableado submarino."},
  [10007] = {id=10007,name="Casimiro III",desc="El rey Casimiro III, el único rey Polaco en obtener el ulterior título de 'El Grande', reinó durante el siglo 14 DC y se estableció como un capaz y decisivo gobernante, apoyándose en sus habilidades de diplomacia y sensible toma de decisiones para mejorar enormemente el bienestar de su gente y la seguridad de su reino. En cuanto a reconstruir una economía mermada, fortalecer la milicia, codificar leyes, y expandir las fronteras de Polonia, Casimiro dejó a su pueblo mucho mejor de como lo encontró - y selló su legado como uno de los reyes más grandes de Polonia."},
  [10008] = {id=10008,name="Sejong Daewang",desc="Sejong Daewang, conocido históricamente como 'Sejong el Grande', fue el cuarto rey de la dinastía Choson en Corea, reinando desde 1418 hasta su muerte en 1450. Considerado por muchos como el más grandioso rey en la historia de Corea, Sejong es conocido por su remarcable aprecio y respeto por la vida humana, tomando numerosos principios civiles y sociales para mejora del bienestar de su gente. Sejong también estimuló avances en la ciencia y la tecnología y es acreditado con la creación del lenguaje escrito Coreano, Hunminjeongeum, conocido en la actualidad como 'Hangul'."},
  [10009] = {id=10009,name="Harald Gormsson",desc="Harald 'Bluetooth' Gormsson fue el rey de Dinamarca por casi 30 años, empezando aproximadamente en 958 DC. Durante su reinado, Bluetooth unío las periféricas tribus de Dinamarca y defendió a su pueblo de las incursiones de Noruega y Alemania, mientras revisaba que se completaran los proyectos de construcción que fortalecieron las defensas de su nación. Bluetooth es igualmente conocido por expulsar las tradiciones paganas Nórdicas de sus antepasados, convirtiéndose en un devoto Cristiano que se esforzó en convertir pacíficamente a la gente de Dinamarca durante su gobierno."},
  [10010] = {id=10010,name="Otto von Bismarck",desc="Otto von Bismarck, también conocido como 'El Canciller de Hierro', es tal vez la figura más representativa en la historia de Alemania. Durante su larga carrera política, Bismarck unificó Alemania y fundó el Imperio Germano. Alemania fue tomada de ser una débil e imprecisa confederación de estados a ser un país unido y poderoso que llegaría a dominar a Europa continental."},
  [10011] = {id=10011,name="Catherine",desc="Catherine La Grande reinó Rusia durante la segunda mitad del siglo 18. Ella supervisó la gran expansión del imperio Ruso, agregando decenas de miles de millas cuadradas de territorio a través de conquista y astuta diplomacia. Una mujer hermosa e inteligente, ella sedujo las mejores mentes de Europa, haciendo de su corte uno de los centros de pensamiento Iluminado en el continente. A pesar de haber nacido en Alemania, Catherine es una de los más grandes gobernantes en la historia Rusa."},
  [10012] = {id=10012,name="Napoleón Bonaparte",desc="Napoleón Bonaparte nació en la isla de Córsega, en donde entró a la academia militar a la edad de 10. En la escuela mostró grandes aptitudes para las matemáticas, historia y geografía así como una total indiferencia a la literatura y las humanidades. A la edad de 14 fue comisionado como sub-teniente en un regimiento de artillería. Cuando se produjo la revolución Francesa, Napoleón se unió a los Revolucionarios y fue nombrado Teniente Coronel de Artillería, en donde rápidamente se hizo de renombre como un comandante exitoso."},
  [10013] = {id=10013,name="Widely Orange",desc="Ampliamente considerado como fundador y libertador de Holanda, William, Príncipe de Orange, también conocido como 'William el Taciturno' lideró una rebelión exitosa a mediados del siglo 16 contra la Dinastía Habsburgo Española quien dominaba los Países Bajos Holandeses. Mientras fue  el fiel sirviente del rey Felipe II de España, William eventualmente llegó a ser el más prominente líder de la rebelión, así como su creciente desagrado por la contínua persecusión Española de protestantes holandeses llegaron a la cabeza. Llevando a sus socios a la causa, William llegó a ser un criminal a los ojos del monarca español y dirigó las fuerzas holandesas a numerosas batallas exitosas contra el rey antes de caer ante una bala de un asesino en 1584."},
  [10014] = {id=10014,name="Gaius Octavius",desc="Gaius Octavius llegaría a ser el primer (y posiblemente el mejor) emperador romano. El terminó un siglo de guerras civiles e inició doscientos años de la Pax Romana (Paz Romana) mientras supervisaba una era dorada en cultura y literatura Romana."},
  [10015] = {id=10015,name="George Washington",desc="George Washington fue uno de un grupo de remarcables hombres que vivió en las Colonias Americanas en la parte final del siglo 18. Tal vez no fue tan pugnaz como John Adams o tan imaginativo como Benjamín Franklin o tan brillante como Thomas Jefferson, Washington tuvo la capacidad de liderar en guerra y en paz. Dirigió la Armada Continental a la victoria contra todas expectativas, y al hacerlo, llevó a su país a la independencia."},
  [10016] = {id=10016,name="Genghis Khan",desc="Genghis Khan (Temuyín) es uno de los líderes más reconocidos y peor temidos en toda la historia de la humanidad. Cientos de estatuas, edificios y productos comerciales llevan su imagen y numerosos trabajos de entretenimiento han sido creados en honor a su vida. Fue el primero en unir las disputadas tribus nómadas del noreste de Asia y fundó el imperio contíguo más grande conocido por el hombre. Tal vez es poco conocido acerca de sus mejoras y avances que hizo en la infraestructura, comercio y tolerancia religiosa. Más que sólo un guerrero, también fue un líder civil inteligente y capáz."},
  [10017] = {id=10017,name="Ahmad al-Mansur",desc="Siendo uno de los líderes con más influencia en la historia de Africa, Ahmad al-Mansur realizó un impacto importante en el futuro de Marruecos. Sirviendo como el sexto sultán de la dinastía Saadí durante el siglo 16 DC, Ahmad al-Mansur fue conocido como un diplomático ingenioso quien apalancó la ubicación estratégica de Marruecos para posicionar su reinado como uno de los mejores jugadores internacionales durante el Renacimiento."},
  [10018] = {id=10018,name="María I",desc="En una dinastía caracterizada por siglos por tragedia y locura, María I es indudablemente la mejor conocida en el mundo, y tal vez la más querida poe el pueblo Portugués. Como reina de Portugal, Brasil y las Algarves, fue conocida como María la Piadosa mientras gobernaba en Portugal, o María la Loca después de huir a Brasil en 1807 de la invasión napoleónica. Incapacitada por su creciente locura, María I murió en Río de Janeiro en 1816."},
  [10019] = {id=10019,name="Oda Nobunaga",desc="Oda Nobunaga fue un caudillo japonés del siglo 16. Fue ambos, un brillante general y un politico astuto - así como un entusiasta de nuevas tecnologías - Nobunaga peleó y trazó su camino a la dominación sobre casi la mitad del Japón feudal. Sus dos tenientes, Toyotomi Hideyoshi y Tokugawa Ieyasu, completarían su trabajo después de su muerte, reunificando a Japón por primera vez en más de un siglo."},
  [10020] = {id=10020,name="Gustavo Adolfo",desc="Gustavo Adolfo, venerado rey sueco y comandante militar, mejor conocido como 'El León del Norte', ascendió al trono a la edad de 17 y rápidamente estableció su reputación como un militar estratégico habilidoso y como un innovador en el arte de la guerra. Heredando una red compleja de conflictos locales y foráneos de su padre, el usurpador rey Carlos IX, Adolfo no dudó en formar alianzas con la que una vez fuera nobleza hostíl, aplastando a los enemigos de Suecia y llevando a su nación a un escalón superior de poder en Europa durante el siglo 17."},
  [10021] = {id=10021,name="Enrico Dandolo",desc="Enrico Dandolo, el 42do Dux de Venecia, fue famoso por su devoción y longevidad, e igualmente infame por su rol pivote en la Cuarta Cruzada que llevó al saqueo de Constantinopla y al colapso del Imperio Bizantino. Lo que hizo a todo esto más remarcable fue el hecho de que Enrico era ciego. Fue enterrado en Santa Sofía."},
  [10022] = {id=10022,name="Isabel I",desc="Isabel I fue la Reina de Castillo y León por 30 años, y junto con su esposo Fernando, preparó el trabajo para la consolidación de España. Debido a su rol en la unificación de España, patrocinio de los viajes de Colón a América, y la finalización de la Reconquista de la Península Ibérica, Isabel es considerada como una de las mas queridas en importantes monarcas en la historia de España."},
  [10023] = {id=10023,name="Alejandro III",desc="Alejandro de Macedonia es incuestionablemente uno de los más grandes caudillos de todos los tiempos. A sus cortos 17 años llevó a su armada a la victoria después de ser victorioso en Europa, Asia, Africa y el Medio Oriente, conquistando cada civilización que pudiera alcanzar."},
  [10024] = {id=10024,name="Mohandas Gandhi",desc="Mohandas Gandhi fue un patriota Indio quien dirigió el movimiento de independencia no-violento de India contra el mandato Imperial Británico entre los inicios y la mitad del siglo 20. Fue pionero 'satyagraha', o resistencia a la tiranía a través de desobediencia civil masiva, un estratagema usado con gran efecto contra el Raj Británico."},
  [10025] = {id=10025,name="Elizabeth I",desc="Elizabeth I fue una remarcable mujer que vivió en una remarcable era. Hermosa, brillante y dura como una roca, sobrevivió e incluso prosperó, reinando en una era en donde las mujeres no eran más que adornos."},
  [10026] = {id=10026,name="Wu Zetian",desc="Así como en la mayoría de las civilizaciones, China ha sido dominada por hombres a través de su historia. Hasta hace poco, a las mujeres se les permitían pocos derechos, y el poder era totalmente negado a ellas. Que una mujer obtuviera el título de Emperatríz, para llegar a ser la persona más poderosa de China, era casi imposible de escuchar. Sólo una persona en toda la historia China fue capaz de hacerlo. Esa persona fue Wu Zetian, una de las más memorables gobernantes - de entre hombres y mujeres - que el mundo ha visto."},
  [10027] = {id=10027,name="Harald Sigurdsson",desc="Herald Sigurdsson recibió el epíteto de 'Hardrada' por su ciudadanía, fue un rey codicioso de Noruega. Además, infructuosamente reclamó el trono Danés en 1048 (y lo intentó varias veces posteriormente) y el trono Inglés en 1066 y este deseo lo llevó al suicidio. Antes de morir en Stamford Bridge, Harald expandió el territorio y viajó largas distancias a países remotos para asesinar."},
  [10028] = {id=10028,name="Qin Shi Huang",desc="Qin Shi Huang fue un político famoso, estratégico y reformador en la historia China. Fue el primero en lograr la unificación de la Gran Cathay con una fuerte mano y fue el primer emperador en el mundo."},
  [10029] = {id=10029,name="Theodore Roosevelt",desc="Theodore Roosevelt fue el presidente más joven en la historia de Norte América. Sus personalidades únicas y políticas reformistas hicieron que fuera uno de los más grandes presidentes en la historia Norte Americana."},
  [10030] = {id=10030,name="Saladín",desc="Saladín fue un famoso militar y político den la Edad Media en el mundo musulmán. Fue el primer sultán de la dinastía Ayyubid Egipcia."},
  [10031] = {id=10031,name="Pedro I",desc="Pedro I nació el 2 de Diciembre de 1825 en Río de Janeiro. Fue el segundo gobernante del Imperio de Brasil. Heredó el trono a la edad de 5 y utilizó el gobierno alterno del partido liberal con el partido conservador y mantuvo el poder de su lado. En 1840 proclamó la liberación de sus esclavos."},
  [10032] = {id=10032,name="Cleopatra VII",desc="Cleopatra VII, conocida en la historia como simplemente Cleopatra, fue la última mujer que reinó en la dinastía de Tolomeo. Trató de suicidarse y terminar la vida de Egipto por medio de una mordedura de áspid (pero las investigaciones muestran que probablemente fue asesinada por Octavio). Entonces Egipto se convirtió en una provincia Romana hasta la destrucción del Imperio Romano del Oeste en el siglo 5."},
  [10033] = {id=10033,name="Reina Victoria",desc="La reina Victoria quien reinara por 64 años fue conocida como la segunda gobernante con mas tiempo de gobernar en la historia británica, después de Elizabeth II. También fue las primera reina de Inglaterra con título de Reina del Reino Unido de la Gran Bretaña e Irlanda y Emperatríz de India. Su reinado de 1837 a 1901 tuvo el pico más fuerte para Bretaña."},
  [10034] = {id=10034,name="Luis XVI",desc="Luis XVI fue el rey de Francia y de Navarra. Su reinado de 72 años fue el más largo con soveranía en la historia europea de acuerdo a cierta evidencia."},
  [10035] = {id=10035,name="Federico I",desc="Federico I nombrado como 'Barba Roja' y Barbarossa fue el rey del Sagrado Imperio Romano y también fue un famoso político y militar en la historia Alemana. Federico I fue conocido como uno de los gobernantes más exitosos en la edad media de Alemania. Durante su reinado el Sagrado Imperio Romano alcanzó la punta de su poder y llegó a ser el país más fuerte en Europa."},
  [10036] = {id=10036,name="Tokugawa Ieyasu",desc="Tokugawa Ieyasu fue un daimyo de Mikawa a finales del período Sengoku Jiday y azuchi-momoyama de Japón. Fue uno de los 3 unificadores de Japón, junto con su Primer Lord Nobunaga y Toyotomi Hideyoshi. Fue un prominente político y militar en la historia Japonesa."},
  [10037] = {id=10037,name="Ashoka",desc="Ashoka fue el tercer emperador Mauria en la antigua India. Su popularidad fue incomparable y su influencia en la historia lo ponen en lo más alto de la lista."},
  [10038] = {id=10038,name="Kublai",desc="Kublai fue un político y militar de Mongolia. Fue el cuarto hijo de Tolui, joven hermano de Mongke llamado Xianzong. También fue el último Khagan del Imperio Mongol y el primer emperador de la Dinastía Yuan."},
  [10039] = {id=10039,name="Pedro I",desc="Pedro I fue el cuarto Zar de la Dinastía Románov Rusa (1682-1725) y el Emperador del Imperio Ruso (1712-1725). Heredó el trono en 1682 y mantuvo el poder y reforma real. Como uno de los gobernantes nombrado 'Gran Emperador', lideró una revolución en política, economía y milicia durante su reinado. Las políticas occidentales que él creó fueron el factor principal para hacer de Rusia un país más fuerte. Pedro el Grande fue conocido como el emperador más prominente en la historia Rusa."},
  [10040] = {id=10040,name="Moctezuma I",desc="Un poderoso guerrero y líder, Moctezuma I ayudó a impulsar la nación Azteca a la grandeza y la gloria. No debe ser confundido con su desafortunado nieto Moctezuma II, quien observó con impotencia como su reinado fue desmantelado por los Conquistadores Españoles."},
  [10041] = {id=10041,name="Kamehameha I",desc="Uno de los líderes más respetados en la historia de Hawái, Kamehameha I fue el primer hombre en unificar las islas Hawaianas, estableciendo el reino independiente de Hawái. Su nacimiento y primeras acciones en su vida cumplieron varias profecías Hawaianas, y fue uno de los mas grandes guerreros en la historia de Hawái. Más allá de su destreza militar, Kamehameha fue un gran estadista, y estableció importantes edictos en tiempo de guerra que han llegado a ser las bases para muchas leyes humanitarias en el mundo."},
  [10042] = {id=10042,name="Gajah Mada",desc="Gajah Mada (holgadamente traducido como el 'General Elefante'), héroe para la gente de Indonesia y símbolo de orgullo nacional y patriotismo, sirvió como Primer Ministro del Imperio Mayapajit de Indonesia para mejor en el siglo 14 DC. A pesar de la poca información que existe en su temprana edad, se conoce que nació como plebeyo, llegando al poder basándose solamente en su intelecto e ingenio - junto con una gran ferocidad en batalla. Sirviendo como guardaespaldas real, Gajah Mada fue instrumental en suprimir una rebelión contra su rey, y fue recompensado con una forma de unirse a la élite gobernante. Eventualmente llegó a tomar la posición de Primer Ministro."}
}
return DLeader