-- J_剧情配置表文字表.xlsx
-- id=自增ID,name=名称,text=对话,
local DStory = {
  [1] = {id=1,name="1",text="Justin, ¡reporta tu ubicación!"},
  [2] = {id=2,name="Justin",text="¡Si, General! La expedición Polaris ha entrada  a la Galaxia Aries. Listos para aterrizar en el Planeta a y empezar la exploración."},
  [3] = {id=3,name="1",text="Recuerda que el objetivo de esta exploración es encontrar la nueva energía que nadie conoce. ¡Mantente seguro y retrocede si es necesario!"},
  [4] = {id=4,name="Justin",text="¡Entendido! ¡Misión recibida!"},
  [5] = {id=5,name="Chuck Li",text="Justin, la nave aterrizará en cualquier momento."},
  [6] = {id=6,name="Justin",text="¡Muy bien! Ubicación: Aries, Planeta a. Empezando el aterrizaje."},
  [7] = {id=7,name="Justin",text="¡Fue realmente inesperado saber que existen criaturas desconocidas en las galaxias Zodiacales! Noak, reporte de pérdidas."},
  [8] = {id=8,name="Noak",text="Gracias a nuestra pronta reacción no hubo muchas pérdidas. Las funciones de la nave son las suficientes para continuar bien con la exploración."},
  [9] = {id=9,name="Justin",text="¡Bien! Establece la prioridad uno en la nave, tendremos tiempos difíciles en este viaje."},
  [10] = {id=10,name="Miguchi Chiko",text="Justin, el Planeta Aries b está justo al frente, pero un objeto desconocido ha sido detectado, ¿continuaremos con el aterrizaje?"},
  [11] = {id=11,name="Justin",text="Trata de contactarlo para que se identifique."},
  [12] = {id=12,name="Miguchi Chiko",text="¡Entendido!"},
  [13] = {id=13,name="Chuck Li",text="¡Alerta! ¡Ataque enemigo y ni siquiera hemos empezado el aterrizaje!"},
  [14] = {id=14,name="Justin",text="¡Calmados! ¡Prepárense para atacar!"},
  [15] = {id=15,name="Christina",text="¡Justin, hemos capturado un navío enemigo! El piloto es un ET."},
  [16] = {id=16,name="Justin",text="¿Es de alguna raza alienígena conocida?"},
  [17] = {id=17,name="Christina",text="No, es desconocida. Por la tecnología de su nave, puedo asegurar que su civilización es más avanzada que cualquier otro alienígena que hayamos conocido."},
  [18] = {id=18,name="Justin",text="Al parecer los rumores eran verdaderos... existen criaturas poderosas en las galaxias Zodiacales. ¡Debemos reforzar nuestra vigilancia a partir de este momento!"},
  [19] = {id=19,name="Chuck Li",text="El Planeta Aries c está al frente. Este es un planeta importante y debe de haber bases de alienígenas."},
  [20] = {id=20,name="Justin",text="¡Alerta máxima! ¡Avancemos cuidadosamente y preparémonos para la batalla!"},
  [21] = {id=21,name="Miguchi Chiko",text="Justin, el Comandante de los alienígenas solicita una reunión en persona."},
  [22] = {id=22,name="Justin",text="¿Es porque nos tienen miedo?"},
  [23] = {id=23,name="Chuck Li",text="También podría ser una trampa, esas criaturas son realmente inteligentes."},
  [24] = {id=24,name="Justin",text="No importa, primero intentaremos la negociación. Lo mejor es evitar una pelea."},
  [25] = {id=25,name="Miguchi Chiko",text="Ok, confirmaré con ellos los detalles de la reunión."},
  [26] = {id=26,name="Justin",text="Chiko, ¿tendremos la conversación en el Planeta Aries d hoy?"},
  [27] = {id=27,name="Miguchi Chiko",text="Si, así es."},
  [28] = {id=28,name="Justin",text="¿Por qué no hemos recibido su respuesta? ¡pareciera una trampa!"},
  [29] = {id=29,name="Chuck Li",text="¡Algo está mal!, debemos retroceder ya."},
  [30] = {id=30,name="Miguchi Chiko",text="Demasiado tarde... la nave enemiga está muy cerca."},
  [31] = {id=31,name="Justin",text="¿Quién eres? ¡no eres alienígena!"},
  [32] = {id=32,name="Jack",text="¡Vaya! parece que nuestros invitados son unos estúpidos. ¡Soy el famoso Capitán Jack!"},
  [33] = {id=33,name="Justin",text="¡Ah... eres un Pirata espacial! no me digas que quieres robarnos."},
  [34] = {id=34,name="Jack",text="Vengo a informarles que éste no es un lugar en el que se puedan quedar... ¡dejen las galaxias Zodiacales y no vuelvan!"},
  [35] = {id=35,name="Justin",text="Vaya, tenemos a un Pirata buscado en todas las galaxias. Parece que olvidaste con quien estás hablando. ¡Flotas, prepárense para atacar!"},
  [36] = {id=36,name="Justin",text="Hmmm, fui muy descuidado al permitir que Jack escapara. No volverá a suceder. ¿Que lo habrá hecho sentirse tan confiado en enfrentar a una flota de la Federación?"},
  [37] = {id=37,name="Chuck Li",text="Debe de haber algo más importante en el área que todavía no hemos visto... como recursos en gran cantidad."},
  [38] = {id=38,name="Justin",text="¡Estoy de acuerdo! Entonces continuaremos con la exploración en Aries."},
  [39] = {id=39,name="Justin",text="¿Por qué nos atacas? ¿Trabajas para Jack?"},
  [40] = {id=40,name="Alice",text="¡Invasores! ¡Aries es nuestro hogar! No les está permitido entrar aquí."},
  [41] = {id=41,name="Miguchi Chiko",text="¡Vaya, tenemos a un alienígena vivo aquí! Y es igual al de la nave que capturamos la vez anterior."},
  [42] = {id=42,name="Alice",text="¡Váyanse ahora, o probarán el sabor de nuestras armas!"},
  [43] = {id=43,name="Justin",text="Capitán, ¡no tenemos intención de pelear contigo! Sólo queremos recolectar algunos recursos para asegurar el funcionamiento de nuestra Fortaleza."},
  [44] = {id=44,name="Alice",text="¡Qué patética mentira! ¡No confiaremos nunca más en ustedes! ¡Están aquí para saquear nuestros recursos y tierras!"},
  [45] = {id=45,name="Justin",text="¿Qué tal si nos tranquilizamos y platicamos civilizadamente? Podemos hacer un intercambio, no hay necesidad de empezar una guerra entre nosotros."},
  [46] = {id=46,name="Alice",text="No me engañan, ustedes están con los Piratas espaciales. ¡Prepárense para morir!"},
  [47] = {id=47,name="Justin",text="¿Nos crees ahora? no estamos del lado de los Piratas. Ellos son criminales y también los estamos buscando."},
  [48] = {id=48,name="Alice",text="¡Me disculpo por mi rudeza! Ustedes no son Piratas... ellos siguen saqueando nuestras tierras. Pero cada vez que queremos atraparlos, ellos ya no están."},
  [49] = {id=49,name="Alice",text="Si llegan a atraparlos, tráiganlos... ¡ustedes serán los invitados de honor de Aries!"},
  [50] = {id=50,name="Justin",text="Ok, ellos les robaron, así que nos robaron a nosotros también. Es nuestro deber atraparlos. ¡Esperen nuestras noticias de su captura!"},
  [51] = {id=51,name="Jack",text="Malditos exploradores, ¿por qué siguen persiguiéndonos?"},
  [52] = {id=52,name="Justin",text="Pensé que ya tenías la respuesta. La Federación ha enviado una orden de arresto. Estamos aquí para ejercer justicia."},
  [53] = {id=53,name="Jack",text="¡Jajaja... que buena broma! ¡Ni siquiera la Guardia Galáctica nos pudo tocar! ¿Qué podría hacernos una pequeña expedición como esta?"},
  [54] = {id=54,name="Justin",text="¿En serio quieres saberlo? Te arrepentirás de haberlo pensado."},
  [55] = {id=55,name="Justin",text="No son buenos en la pelea, pero sí son expertos en escapar."},
  [56] = {id=56,name="Noak",text="Tienen armas normales, pero sus aceleradores están modificados... tienen una aceleración increíble."},
  [57] = {id=57,name="Noak",text="Eso explica el por qué los sujetos de Aries, e incluso la Guardia Galáctica no pudo atraparlos. Las cosas se están complicando."},
  [58] = {id=58,name="Justin",text="Entonces necesitamos un plan para llevarlos a un lugar del que no puedan escapar."},
  [59] = {id=59,name="Jack",text="¡Maldición, otra vez ustedes!"},
  [60] = {id=60,name="Justin",text="Hemos venido a traer justicia. ¡Serás juzgado!"},
  [61] = {id=61,name="Jack",text="¡Wow, otra vez con los mismos cuentos! ¿Quién sabe lo que en verdad quieren? Además, ¿creen que pueden acercarse a mi nave?"},
  [62] = {id=62,name="Justin",text="No lo sé, pero sí puedo decirte que el que ríe al último ríe mejor."},
  [63] = {id=63,name="Jack",text="¿Cómo es posible? ¿Cómo es que pudiste atraparme?"},
  [64] = {id=64,name="Justin",text="Vaya, ¿a quién tenemos aquí? ¡Nada menos que al famoso Capitán Jack! ¿Cómo está tu acelerador?... ¿te gustó nuestro campo magnético?"},
  [65] = {id=65,name="Jack",text="¿Cómo sabían que yo estaría en este lugar?"},
  [66] = {id=66,name="Justin",text="¿Es difícil de adivinar? Supimos que recibiste un mensaje informando que aquí había una enorme cantidad de recursos... ¿sabes quién lo envió?"},
  [67] = {id=67,name="Jack",text="¿¡Fueron ustedes!? Pero es muy temprano para celebrar su victoria."},
  [68] = {id=68,name="Justin",text="Ya tienen a Jack... ¿qué hay de nuestro acuerdo?"},
  [69] = {id=69,name="Alice",text="De ahora en adelante pueden recolectar recursos del Planeta Aries π. A cambio recibiremos el 50% de los recursos que recolecten."},
  [70] = {id=70,name="Justin",text="¡Hecho! Esperemos que nuestra amistad sea eterna."},
  [71] = {id=71,name="Alice",text="Solo tengan cuidado, este lugar está lleno de caos y guerras. Ladrones e invasores podrían llegar en cualquier momento."},
  [72] = {id=72,name="Chuck Li",text="¡Ataque enemigo! ¿vienen por Jack?"},
  [73] = {id=73,name="Jack",text="¡Jajaja! Les dije que era muy rápido para celebrar su victoria. Cruyff, llegas un poco tarde."},
  [74] = {id=74,name="Cruyff",text="Jack, eres un idiota... siempre causando problemas. Esta es la última vez que salvo tu pellejo."},
  [75] = {id=75,name="Justin",text="¡Invasores, identifíquense! Jack es un criminal buscado por las Federaciones. ¿Quieren oponerse a la Federación?"},
  [76] = {id=76,name="Cruyff",text="La Federación... ¡Uy que miedo! ¡La mafia Rusa no le teme a nada!"},
  [77] = {id=77,name="Justin",text="¡¿Mafia Rusa?! Flota, rápido... ¡formación!"},
  [78] = {id=78,name="Cruyff",text="Quédense con lo que queda de sus naves y sus dignidad. No tengo tiempo para más juegos. Pero la próxima vez no tendrán suerte de quedar con vida."},
  [79] = {id=79,name="Alice",text="Ellos son más peligrosos que Jack. Aún Tauro está bajo su control. Si vas para allá, cuídate de todo."},
  [80] = {id=80,name="Justin",text="Gracias por recordármelo, pero el que nada arriesga, nada gana. Iremos a Tauro... ¡veamos que nos tienen preparado!"},
  [81] = {id=81,name="Justin",text="Comandante, ya está construida la base para recolección de Energía en el Planeta Aries π. Estamos planeando dirigirnos a Tauro."},
  [82] = {id=82,name="1",text="Se dice que la Mafia Rusa tiene el control de Tauro. Vayan con cuidado."},
  [83] = {id=83,name="Justin",text="No te preocupes, seremos cautelosos."},
  [84] = {id=84,name="Chuck Li",text="Justin, el planeta Tauro c está al frente, ¿aterrizamos según el plan?"},
  [85] = {id=85,name="Justin",text="Aterricemos, y estén listos para la batalla. Supongo que nuestro anfitrión nos está esperando."},
  [86] = {id=86,name="Justin",text="Ya lo esperaba... ¡ésta es su ’cálida bienvenida’!"},
  [87] = {id=87,name="Ansel",text="Así que ustedes son los malos tipos de los que habló Cruyff."},
  [88] = {id=88,name="Christina",text="Guau, ¿acaso escuché mal? ¿tipos malos? ¡Ellos son los que deben ser juzgados por la Federación!"},
  [89] = {id=89,name="Ansel",text="¡Paren de acusar al gran Cruyff! El nos ayuda a recolectar recursos y comida. Ustedes no son bienvenidos... ¡váyanse!"},
  [90] = {id=90,name="Christina",text="¿Qué?, ¿por qué confían tanto en Cruyff? No lo entiendo."},
  [91] = {id=91,name="Justin",text="Los aborígenes son sencillos. Cruyff ganó primero su confianza. Es difícil que nosotros encajemos ahora."},
  [92] = {id=92,name="Chuck Li",text="Y están bien armados, no podemos atacar directamente."},
  [93] = {id=93,name="Justin",text="La única forma de lograr la victoria es revelando al verdadero Cruyff."},
  [94] = {id=94,name="Barack",text="Soy el Comandante de la Patrulla Galáctica. ¡Atacantes, identifíquense!"},
  [95] = {id=95,name="Justin",text="Disculpe nuestra rudeza... fue un error. Somos la Expedición Polaris y venimos a las Galaxias Zodiacales para explorar. Pensamos que ustedes eran parte del grupo de Cruyff"},
  [96] = {id=96,name="Barack",text="¿Te refieres al Cruyff de la Mafia Rusa? ¿Sabes dónde está?"},
  [97] = {id=97,name="Justin",text="Si. El pirata espacial Jack y Cruyff están escondidos en Tauro."},
  [98] = {id=98,name="Barack",text="¡Vaya, dos peces grandes! ¿Están dispuestos a colaborar con nosotros y arrestarlos?"},
  [99] = {id=99,name="Justin",text="¡Con gusto! Pero primero necesitaremos quebrantar su Federación en Tauro, o será muy difícil derrotarlos."},
  [100] = {id=100,name="Barack",text="Eso no es problema. Fingiremos atacar la base de Tauro. Cruyff no vendrá a poyar, pero en su lugar llegarán ustedes para ayudar a Tauro en el momento justo."},
  [101] = {id=101,name="Justin",text="¡Pero Tauro está muy bien armado! Tal vez ni siquiera tendrán oportunidad de crear una sólida amenaza."},
  [102] = {id=102,name="Barack",text="No se preocupen, tenemos nuestro plan."},
  [103] = {id=103,name="Ansel",text="¿Por qué nos atacan? ¡Somos aborígenes pacíficos en Tauro!"},
  [104] = {id=104,name="Barack",text="¡Somos la Patrulla Galáctica, venimos a arrestar a Cruyff!"},
  [105] = {id=105,name="Ansel",text="Cruyff es nuestro amigo, no permitiremos que se lo lleven."},
  [106] = {id=106,name="Barack",text="Entonces, tendremos que resolver esto en batalla."},
  [107] = {id=107,name="Ansel",text="No tienen idea de lo poderosos que somos. Te lo mostraré... ... ¡ah! ¡mi cabeza! ¡¿que hiciste?!"},
  [108] = {id=108,name="Barack",text="¿Sorprendido? Estas son ondas magnéticas. Pueden perturbar tu sistema nervioso central... tu principal debilidad."},
  [109] = {id=109,name="Ansel",text="Cruyff, ¡ayúdanos!"},
  [110] = {id=110,name="Justin",text="Cruyff ha huido. Permítanos ayudarles."},
  [111] = {id=111,name="Ansel",text="Gracias... no lo hubiéramos logrado sin ustedes."},
  [112] = {id=112,name="Justin",text="En verdad queremos ser sus amigos. Y también necesitamos tener a Cruyff bajo arresto. ¿Saben dónde está?"},
  [113] = {id=113,name="Ansel",text="En la necesidad se demuestra la amistad. ¡Ustedes son nuestros amigos! Cruyff está en el Planeta Tauro l, ahí lo encontrarán."},
  [114] = {id=114,name="Justin",text="Gracias. Hablaremos de nuestra cooperación después de derrotar a Cruyff"},
  [115] = {id=115,name="Justin",text="Cruyff está en el Planeta Tauro l. ¡Vayamos a atraparlo!"},
  [116] = {id=116,name="Barack",text="Estamos listos para el ataque."},
  [117] = {id=117,name="Justin",text="¡Grandioso! A mi señal... ¡ataquen!"},
  [118] = {id=118,name="Cruyff",text="No me esperaba esto... ser derrotado por ustedes."},
  [119] = {id=119,name="Barack",text="Has causado muchas tragedias. La Federación ha emitido una orden de arresto en tu contra. ¡Esta vez serás juzgado!"},
  [120] = {id=120,name="Cruyff",text="¿Crees que puedes llevarme? Bermuda no lo permitirá... espera y verás."},
  [121] = {id=121,name="Justin",text="¿Qué es Bermuda?"},
  [122] = {id=122,name="Cruyff",text="Pronto lo sabrás. Las galaxias Zodiacales no son tan simples como piensas. ¡Te arrepentirás!"},
  [123] = {id=123,name="Justin",text="¡Gracias por ayudar, ya tenemos a Cruyff!"},
  [124] = {id=124,name="Ansel",text="Pero, tampoco tenemos a nadie que nos ayude a recolectar recursos. Tenemos tecnología avanzada, pero no sabemos de recolección."},
  [125] = {id=125,name="Justin",text="Entonces les ayudaremos. Ustedes obtendrán el 50% de la producción, ¿qué les parece?"},
  [126] = {id=126,name="Ansel",text="¿En serio? ¡Eso sería grandioso! Cruyff sólo nos daba el 10%. Ustedes son muy amables."},
  [127] = {id=127,name="Justin",text="Espero que pasemos buenos momentos juntos."},
  [128] = {id=128,name="Chuck Li",text="¡Enemigo detectado! Tenemos que defendernos."},
  [129] = {id=129,name="Justin",text="¡Atacantes, identifíquense!"},
  [130] = {id=130,name="Clif",text="¿Cómo se atreven a enfrentar a Bermuda? ¡Serán devorados por nuestra furia!"},
  [131] = {id=131,name="Justin",text="Así que tú eres Bermuda del que habló Cruyff."},
  [132] = {id=132,name="Clif",text="Esta es sólo una advertencia. Ustedes no son rival para Bermuda. No se entrometan o tendrán problemas."},
  [133] = {id=133,name="Justin",text="¿Tomo eso como una declaración de guerra? ¿Quién es tu jefe?"},
  [134] = {id=134,name="Clif",text="Tendrás tu respuesta si se atreven a venir a Géminis."},
  [135] = {id=135,name="Christina",text="¿En verdad iremos a Géminis? ¡Seguro es una trampa!"},
  [136] = {id=136,name="Justin",text="Nuestra misión es explorar las galaxias Zodiacales, iremos a Géminis sin importar la situación."},
  [137] = {id=137,name="Christina",text="¿Debemos cancelar la exploración de Géminis debido a Bermuda?"},
  [138] = {id=138,name="Justin",text="Siempre habrá problemas durante las exploraciones. No podemos retirarnos cada vez que nos encontramos uno. Así que, ¡vamos!"},
  [139] = {id=139,name="Christina",text="¡Sí señor! ¡Seguiremos juntos en esto!"},
  [140] = {id=140,name="Jesco",text="¿Quién eres? ¿Yamaguchi-gumi?"},
  [141] = {id=141,name="Justin",text="Somos la Expedición Polaris, no el Yama... lo que sea. ¿Quién eres tú?"},
  [142] = {id=142,name="Jesco",text="Soy el Comandante Jesco, de la Expedición de Adis Battlestar."},
  [143] = {id=143,name="Justin",text="¡Es un placer saludarlo, Comandante Jesco! ¿Qué es el  Yama...chi...mee... que acaba de mencionar?"},
  [144] = {id=144,name="Jesco",text="Yamaguchi-gumi... una banda japonesa. Justo recibimos uno de sus ataques y escaparon."},
  [145] = {id=145,name="Justin",text="Al parecer también le paso algo malo a Géminis. Jesco, ¿qué te parece si exploramos juntos?"},
  [146] = {id=146,name="Jesco",text="¡Estaré más que felíz de poder hacerlo!"},
  [147] = {id=147,name="Jesco",text="El Planeta Géminis f está al frente. Con cuidado, fuimos emboscados aquí la última vez."},
  [148] = {id=148,name="Justin",text="¡Atención todos! Algo está muy extraño aquí... avancemos despacio."},
  [149] = {id=149,name="Clif",text="¡Vaya, vaya, vaya, miren quien llegó!. Capitán Justin... que gusto verte de nuevo."},
  [150] = {id=150,name="Justin",text="¡Tú!... ¡Bermuda!"},
  [151] = {id=151,name="Jesco",text="El es el jefe de los Yamaguchi-gumi. El fue quien nos atacó previamente."},
  [152] = {id=152,name="Justin",text="¿Quién rayos eres? ¿Qué es Bermuda?"},
  [153] = {id=153,name="Clif",text="Ya que entraron a Géminis, pasaremos un muy buen tiempo jugando. ¡Nunca saldrán de aquí!"},
  [154] = {id=154,name="Justin",text="¡Con cuidado! Ya hemos sufrido 4 ataques de ellos y aún no hemos podido acertar un buen disparo."},
  [155] = {id=155,name="Jesco",text="¡Desaparecen en cuanto lanzan su ataque! El sistema no tiene oportunidad de detectarlos, ¿cómo lo hacen?"},
  [156] = {id=156,name="Williams",text="Supongo que habrá algunos agujeros por aquí. A través de ellos pueden llegar e irse después de atacar."},
  [157] = {id=157,name="Jesco",text="¿Agujeros? ¿Entonces cómo podremos evitar sus ataques?"},
  [158] = {id=158,name="Williams",text="Debería de haber IEM (Interferencia Electro Magnética) alrededor de esos agujeros. Podemos usar un detector de EMI, pero no aseguro que funcione."},
  [159] = {id=159,name="Jesco",text="¡Cuidado, el enemigo apareció de nuevo! Están frente a nosotros."},
  [160] = {id=160,name="Jesco",text="¡Esto no sirve de nada! ¡No podemos acertar ningún ataque! ¡Sugiero que dejemos Géminis, o todos caeremos!"},
  [161] = {id=161,name="Justin",text="La retirada parece una buena opción, pero estamos en el centro de Géminis, así que no será fácil."},
  [162] = {id=162,name="Williams",text="Tranquilos, acabo de activar el detector de IEM, podremos ver el agujero cuando esté por ocurrir."},
  [163] = {id=163,name="Williams",text="Entonces podremos pasar a través de él antes de que nos ataquen."},
  [164] = {id=164,name="Jesco",text="Pero no sabemos a dónde nos llevará el agujero. ¿Y si del otro lado del agujero está su base?"},
  [165] = {id=165,name="Justin",text="Hay otra forma... podremos emboscarlos a través del agujero y arrasarlos."},
  [166] = {id=166,name="Jesco",text="¡Eso suena bien! ¡Podemos hacerlo!"},
  [167] = {id=167,name="Justin",text="¡Por fin te tenemos! No hay lugar donde puedas esconderte."},
  [168] = {id=168,name="Clif",text="¡Inaudito! Pudieron encontrar el agujero y emboscarnos."},
  [169] = {id=169,name="Justin",text="¿Cómo se siente ser la presa? Ahora se han invertido los papeles y es nuestro turno de atacar."},
  [170] = {id=170,name="Clif",text="Veamos si tienes el poder para hacerlo."},
  [171] = {id=171,name="Clif",text="¿Una flota de exploración con tanto poder de disparo? ¿Cómo es que pudieron derrotar una flota élite de los Yamaguchi-gumi?"},
  [172] = {id=172,name="Justin",text="Esta es una combinación de dos flotas de exploración. No puedes pelear contra nosotros. Ya es hora de llevarte a la Patrulla Galáctica."},
  [173] = {id=173,name="Clif",text="¡Pobres ilusos! ¿Creen que pueden atraparnos aquí? Conocemos este lugar como la palma de nuestras manos. さようなら (adiós)"},
  [174] = {id=174,name="Jesco",text="¡Maldición, los perdimos! Había otro agujero oculto ahí."},
  [175] = {id=175,name="Justin",text="No importa. Estoy seguro que los veremos muy pronto."},
  [176] = {id=176,name="Chuck Li",text="Justin, hemos entrado en la galaxia Cáncer. El Planeta c está al frente."},
  [177] = {id=177,name="Justin",text="Ya que nos separamos de Jesco todo ha estado muy silencioso. No nos hemos encontrado siquiera con los Yamaguchi-gumi."},
  [178] = {id=178,name="Chuck Li",text="Así es, ¿no sientes la atmósfera algo extraña? Se siente muy... ¿triste?"},
  [179] = {id=179,name="Justin",text="Siento lo mismo. Con cuidado, ¡algo no está bien!"},
  [180] = {id=180,name="Justin",text="¿Quiénes son?, ¡identifíquense y denos su propósito!"},
  [181] = {id=181,name="Clarence",text="Somos aborígenes de la Galaxia Cáncer. Vivimos aquí."},
  [182] = {id=182,name="Justin",text="¿Por qué lucen tan cohibidos?"},
  [183] = {id=183,name="Clarence",text="Nuestro patriarca ha sido capturado por Mauris. Nuestra gente está bajo las órdenes de Mauris y recolectan energía para ellos."},
  [184] = {id=184,name="Justin",text="No se preocupen, les ayudaremos a liberar a su patriarca."},
  [185] = {id=185,name="Chuck Li",text="El Planeta Cáncer f está al frente. Como nos informaron, su patriarca debe está ahí. Tienen una fuerte defensa y no será fácil sacarlo."},
  [186] = {id=186,name="Justin",text="Cierto... necesitamos un plan."},
  [187] = {id=187,name="Chuck Li",text="Parece que ya tienes un plan."},
  [188] = {id=188,name="Justin",text="Pretenderemos ser un equipo de exploración y les causaremos unos pequeños problemas. Los distraeremos y en seguida entramos en acción."},
  [189] = {id=189,name="Chuck Li",text="Creo que esa es la mejor forma... hagámoslo."},
  [190] = {id=190,name="Justin",text="Tienen demasiados guardias en el interior. Hemos fallado."},
  [191] = {id=191,name="Chuck Li",text="Ahora estarán más atentos a amenazas. Se ha complicado la situación... ¿no tienes otro plan?"},
  [192] = {id=192,name="Justin",text="No tengo plan. Podremos pedir ayuda a la Patrulla Galáctica, pero por ahora tenemos que escapar."},
  [193] = {id=193,name="Justin",text="¡Hey Barack, amigo! ¡Aquí estás! Tenemos unos problemas y necesitamos de tu ayuda."},
  [194] = {id=194,name="Barack",text="¿Qué pasa?, dime."},
  [195] = {id=195,name="Justin",text="El patriarca de los nativos de Cáncer fue hecho prisionero de Mauris, y el pueblo es ahora su esclavo."},
  [196] = {id=196,name="Barack",text="¿Mauris? ¿El pez gordo de los traficantes de drogas? He estado buscándolo por un largo tiempo. Tal vez lo mejor es cooperar nuevamente."},
  [197] = {id=197,name="Chuck Li",text="Capitán, un equipo enemigo está aproximándose."},
  [198] = {id=198,name="Barack",text="Enfrentémoslos y saquémosles algo de información."},
  [199] = {id=199,name="Barack",text="El lugar donde Mauris se esconde está al frente. Tomaremos el primer ataque. Justin, ustedes cubran la retaguardia y no los dejen escapar."},
  [200] = {id=200,name="Justin",text="Seguro... tenemos que ganar esta vez."},
  [201] = {id=201,name="Maurice",text="¡Barack! ¡Tú! ¿Cómo es que estás aquí?"},
  [202] = {id=202,name="Barack",text="¿Sorprendido? Esta vez te atraparé. No hay lugar donde puedas esconderte esta vez."},
  [203] = {id=203,name="Maurice",text="Tengo al patriarca conmigo. No vivirá si yo muero."},
  [204] = {id=204,name="Barack",text="No quieres hacer eso. Créeme."},
  [205] = {id=205,name="Maurice",text="Puedes intentarlo... ¡o quitarte de mi camino ahora!"},
  [206] = {id=206,name="Justin",text="Maurice ha escapado, aún en esta situación. Afortunadamente tenemos al patriarca."},
  [207] = {id=207,name="Barack",text="No se preocupen, no puede escapar de mí."},
  [208] = {id=208,name="Barack",text="Puse un rastreador en su nave. Sabemos que está cerca."},
  [209] = {id=209,name="Justin",text="Nada es mejor que eso, en realidad ’no tiene lugar en donde esconderse’."},
  [210] = {id=210,name="Clarence",text="¡Gracias, salvaron a nuestro patriarca! Estamos en deuda eterna con ustedes."},
  [211] = {id=211,name="Justin",text="No hay problema, esto es lo que hacemos."},
  [212] = {id=212,name="Clarence",text="Serán nuestros amigos por siempre. Si necesitan energía, siéntanse libres de tomarla."},
  [213] = {id=213,name="Justin",text="Guau, eso es grandioso... ahora es mi turno de agradecerles."}
}
return DStory