  [505] = {id=505,title="Nivel del Comandante",desc="Al actualizar el nivel del Comandante puedes obtener poderosas propiedades de tonificación y habilidades especiales",content="1. Atacar en una instancia de batalla puede darte EXP de Comandante. \n2. Eliminar a piratas galácticos puede darte EXP de Comandante. \n3. La Voltereta de Suerte, Misiones Diarias y Actividades pueden darte EXP de Comandante."},
  [506] = {id=506,title="Nivel de la Civilización",desc="Al actualizar el nivel de la Civilización puedes mejorar el límite da la formación sencilla de navíos",content="1. Al actualizar las construcciones puedes obtener Prosperidad. La Prosperidad puede mejorarse cuando se cumplan los requerimientos de la mejora."},
  [507] = {id=507,title="Nivel General",desc="Al actualizar el nivel General puedes mejorar el límite de la formación sencilla de navíos",content="1. Para actualizar el nivel del Comandante necesitas Orden General, el cual obtienes al eliminar piratas galácticos."},
  [508] = {id=508,title="Nivel de Rango Militar",desc="Al actualizar el nivel del rango militar puedes actualizar el límite de la formación sencilla de navíos y ganar propiedades de tonificación",content="1. Al eliminar navíos de otros jugadores puedes obtener valor de Explotación. A mayor nivel de los navíos eliminados, mayor es el valor de explotación que puedes obtener. \n2. El rango militar puede actualizarse cuando el valor de Explotación cumpla los requerimientos."},
  [509] = {id=509,title="Nivel de VIP",desc="Al actualizar y activar el VIP puedes obtener poderosas propiedades de tonificación y varios privilegios",content="1. Para actualizar el nivel de VIP se necesitan puntos VIP, los cuales obtienes en las misiones diarias, batallas y contra los piratas galácticos. \n2. Al usar diamantes puedes comprar una gran cantidad de puntos VIP rápidamente. \n3. Los privilegios de VIP sólo tienen efecto al activar el VIP."},
  [510] = {id=510,title="Poder de Combate de Fragatas",desc="Las Fragatas son efectivas contra Interceptores y Destructores enemigos",content="1. Las Fragatas pueden crearse en la Fábrica de Navíos. Usa diamantes para acelerar la velocidad de creación. \n2. Actualiza la Fábrica de Fragatas para mejorar su nivel."},
