  [2048] = {id=2048,key="key_2048",data="Consejos:"},
  [2049] = {id=2049,key="key_2049",data="Recompensas de primera compra"},
  [2050] = {id=2050,key="key_2050",data="El saldo de sobres rojos se ha restaurado."},
  [3001] = {id=3001,key="key_3001",data="Comandante:\nAcabas de activar los escudos. Durarán por {1} horas y expirarán a las {2}."},
  [3002] = {id=3002,key="key_3002",data="Comandante:\nTus escudos han expirado. Podrías ser detectado o atacado por otro Comandante desde este momento."},
  [3003] = {id=3003,key="key_3003",data="{1} ha activado sus escudos. No puedes detectarlo o atacarlo. Las flotas han regresado."},
  [3004] = {id=3004,key="key_3004",data="{1} ha activado la anti-detección, no puedes detectarlo."},
  [3005] = {id=3005,key="key_3005",data="Felicitaciones, te ha unido a la Federación, ¡recibirás un regalo en tu correo! Usa la tele-transportación de la Federación y mueve tu Fortaleza más cerca de tus aliados, lo que te permitirá ayudarlos para construir, intercambiar recursos, defender y experimentar las Guerras de la Federación. \nDespués de usar la tele-transportación de la Federación, puedes mover la Fortaleza a una posición más cercana a tu líder.\n¡Mueve tu Fortaleza más cerca de tus aliados y logra grandes cosas!"},
  [3006] = {id=3006,key="key_3006",data="Felicidades, has creado tu Federación, ¡recibirás un regalo en tu correo! ¡Usa la tele-transportación avanzada para mover tu Fortaleza al lugar donde quieres desarrollar tu Federación!\nInvita a más aliados a tu federación, desarróllala y logra grandes cosas."},
  [3007] = {id=3007,key="key_3007",data="Comandante, \n Debido a tu dedicación, {1} cambió el Rango de tu Federación de {2} a {3}. ¡Sigue esforzándote en los eventos, donaciones y ayudando a otros en la Federación!"},
  [3008] = {id=3008,key="key_3008",data="Comandante, \n Debido a tu dedicación, {1} cambió el Rango de tu Federación de {2} a {3}. Ahora tienes permisos para Promover o Degradar miembros de la Federación. ¡Sigue esforzándote en los eventos, donaciones y ayudando a otros en la Federación!"},
  [3009] = {id=3009,key="key_3009",data="Comandante, \n Debido a tu dedicación, {1} cambió el Rango de tu Federación de {2} a {3}. Ahora tienes permisos para desbloquear la actividad Guerra Espacial e Investigar en Tecnologías de la Federación. ¡Sigue esforzándote en los eventos, donaciones y ayudando a otros en la Federación!"},
  [3010] = {id=3010,key="key_3010",data="Comandante, \n Debido a tu poca acción, {1} cambió el Rango de tu Federación de {2} a {3}. ¡No te desanimes y sigue esforzándote en los eventos, donaciones y ayudando a otros en la Federación!"},
  [3011] = {id=3011,key="key_3011",data="¡Felicidades! Tu Federación ha desbloqueado la nueva Fortaleza! ¡Ven a construirla ya!"},
  [3012] = {id=3012,key="key_3012",data="Fuiste expulsado de la Federación por {1}. Cree que no aportaste a la Federación."},
  [3013] = {id=3013,key="key_3013",data="¡Felicidades a {1} por ser el nuevo Líder! Esperamos que el nuevo líder nos lleve a un futuro próspero."},
  [3014] = {id=3014,key="key_3014",data="El líder de la Federación ha estado fuera de línea por largo tiempo. {1} se ha convertido en el nuevo líder. Esperamos que nos lleve a un futuro próspero."},
  [3015] = {id=3015,key="key_3015",data="{1} acaba de empezar la actividad de Guerra Espacial en el tiempo del sistema {2}. ¡Prepárense a cumplir el reto!"},
  [3016] = {id=3016,key="key_3016",data="Acuerdo de la Actividad\nLa actividad de la Federación acaba de terminar. Si te retiras de la Federación no recibirás recompensas.\n Escena terminada de la actividad: {1}\nPuntos recibidos: {2}\nTambién puedes mejorar la puntuación al ayudar aliados. Puedes revisar el detalle en el Centro de Actividades."},
  [3017] = {id=3017,key="key_3017",data="¡Felicidades! Tu aliado {1} acaba de eliminar a {2}, ¡todos los miembros de la Federación recibirán recompensas!"},
  [3018] = {id=3018,key="key_3018",data="Comandante: \n ¡felicitaciones por actualizar tu Fortaleza! Puedes construir la Fábrica de Cruceros y la Fábrica de Destructores. \n Después de construir la Fábrica de Cruceros se desbloqueará la Fábrica de Forjas donde podrás forjar equipo. \n1. El equipo del Comandante se divide en 6 partes, y cada parte puede usar un equipo. \n2. El equipo del Comandante se divide en 6 niveles de calidad: blanco, verde, azul, púrpura, rojo y naranja. El naranja es el de mejor calidad. La calidad afecta el valor del equipo. \n3. Por cada 10 niveles se puede hacer un nuevo grado para el equipo. A mayor nivel del equipo, mayor es su valor."},
  [3019] = {id=3019,key="key_3019",data="Comandante: \n Te has registrado por más de 3 horas así que hemos llamado a la Tele-transportación del Novato. No podrás viajar a otras galaxias. \n\nRequerimientos para moverte a otras galaxias:\nLos Comandantes registrados en los últimos 3 días y que tengan una Fortaleza menor a nivel 6 se podrán tele-transportar a otras galaxias.\n\n¡Disfruta!"},
  [3020] = {id=3020,key="key_3020",data="¡Felicidades, has obtenido una utilería de 'modificar el nombre' y una de 'cambiar de imágen'! \n Cómo cambiar el nombre: usa 'modificar el nombre' desde el inventario para ir a la información del Comandante. Toca sobre el nombre y modifícalo. \n Cómo cambiar la imágen: usa 'cambiar de imágen' desde el inventario o toca sobre los detalles del Comandante. Toca sobre el botón 'Cambiar' para cambiar de imágen. \n ¡Usa un gran nombre y utiliza una fabulosa imágen! \n\n Has construido el Centro de Combate. Puedes construir armas de defensa ahí, las cuales te ayudarán a defenderte de invasiones enemigas."},
  [3021] = {id=3021,key="key_3021",data="¡Comandante, al activar el VIP obtendrás un fuerte efecto de buff! ¡El VIP puede darte una gran variedad de beneficios y hacer tu flota más feroz! \n Acepta nuestro regalo. Ve al sistema de VIP y actívalo, ¡experimentarás un poder sin comparación! \n Incrementa los puntos de VIP para incrementar el nivel de VIP. \n Puedes recibir puntos de VIP al ingresar diariamente o al usar las utilerías de puntos VIP."},
  [3022] = {id=3022,key="key_3022",data="Comandante: \n ¡felicitaciones por actualizar tu Fortaleza! Puedes construir el Centro de Contacto. \n Después de construir el Centro de Contacto y de unirte a una Federación podrás obtener ayuda de los aliados, acelerar la construcción de edificaciones, navíos, etc. \n Después de construir el Centro de Contacto los aliados te ayudarán. Te enviarán navíos a tu fortaleza y pelearán contra invasores."},
  [3023] = {id=3023,key="key_3023",data="Comandante: \nJefe, puedes construir un Centro de Estrategia. Con ésto podremos formar una División de Justicia. \n Atacar es siempre mejor que estar en defensa pasiva. Ahora que tenemos una armada poderosa, hay que tomar la oportunidad para atacar al enemigo. Pero no olvides detectar las defensas y recursos enemigos. Un buen Comandante no se lanzaría a un precipicio."},
  [3024] = {id=3024,key="key_3024",data="Comandante: \nYa puedes construir el Centro de Estratégia, a través del cual podemos agregar y formar una armada invencible. Esas criaturas débiles temblarán frente a nosotros.\nEl ataque es la mejor defensa. Ahora que tenemos flotas poderosas aprovechemos para atacar algunos enemigos. No olvides detectar los recursos y defensas enemigas antes de ello. Un buen Comandante nunca se acelerará en sus acciones."},
  [3025] = {id=3025,key="key_3025",data="Comandante, \nCon tus logros has hecho que tu gente no pasara hambre o frío. Ahora es tiempo de ser creyente en Dios. Construye los 'Remanentes de la Civilización', permite que la gente ore ahí y Dios nos dará gloria.\nLos 'Remanentes de la Civilización' te brindarán todo tipo de recursos, pero puedes pedir aquellos que necesites con urgencia. Consejo: trata de recolectar Acero lo más posible, el cuál es necesario para crear equipo, y no será saqueado por otros jugadores."},
  [3026] = {id=3026,key="key_3026",data="Comandante, \n¡En verdad es un día muy emocionante! Los consultores técnicos de otras galaxias dieron a conocer una nueva tectonología de refinamiento de energía. Sólo necesitamos construir una 'Planta de Energía' para recolectarla, y tenemos un área libre fuera de nuestra base para ello.\n\nLa función de refinar se desbloqueará cuando actualices tu 'Fábrica de Forja' a nivel 10, entonces podrás refinar tu equipo para hacerlo de mejor calidad.\n\nUna cosa más, ahora somos más poderosos y una recolección normal de recursos no satisface nuestras demandas. Por suerte en el 'Centro de Actividades' podemos encontrar algunas con recompensas abundantes. No solo hay actividades regulares como la 'Guerra Espacial' o la 'Batalla de Dragón', pero encontrarás otras actividades también...no le quites el ojo de encima."},
  [3027] = {id=3027,key="key_3027",data="Estimados adultos Commander, su fortaleza ataque enemigo, se encuentra en el estado de combustión! Alto se trasladó a la ciudad para usar accesorios van al encuentro de tu liga, puede aceptar mejor la Unión por los aliados de protección para ayudar! \n1, cuando su fortaleza enemiga fue atacado y los defensores de fallar, tu ciudad estará en el estado de combustión. Estado de combustión, tu ciudad continuará disminuyendo el valor de defensa de la ciudad, cuando su valor de defensa de la ciudad se convierte en '0', su ciudad será destruida; \n2, utilice la guarda guerra, la defensa puede efectivamente evitar pérdidas; \n3, el satélite Ver estación de invadir la situación del enemigo, a desarrollar la estrategia de defensa más eficaz; \n4, tratar de construir más centros de reparación, lo que reduce las pérdidas de la flota tanto como sea posible; \n5, solicite asistencia de aliados, es uno de los medios eficaces. Por supuesto, usted tiene tanto como sea posible para ayudar a sus aliados; \n6, actualizar el almacén, puede proteger eficazmente sus recursos no están saqueado enemigo; \n7, antes de ser atacado, trata de quedarse sin recursos, oa través de un transacciones de centros comerciales sus aliados a fin de evitar la pérdida de recursos."},
  [3028] = {id=3028,key="key_3028",data="Comandante, debido a la capacidad limitada de navíos dañados, acabas de recibir {1} navíos dañados, con {2} destruidos por tanto daño."},
  [3029] = {id=3029,key="key_3029",data="Comandante, has perdido muchas flotas recientemente. Tenemos una recompensa que puede consolarte. Cuando tengas batallas en las galaxias puedes unirte a una Federación y unirte con algunos aliados para ayudarse mutuamente y evitar la derrota. Puedes revisar la ubicación del líder y de los aliados en los mapas de la galaxia. Por el momento puedes intentar construir y actualizar el Centro de Estrategia para evitar que tus naves sean destruidas."},
  [3030] = {id=3030,key="key_3030",data="Felicidades por actualizar tu Fortaleza a nivel {1}, ¡la función de Formación se ha desbloqueado! Toca sobre icono de la Flota en la esquina inferior izquierda y establece tu formación en la Interfaz de Detalles de la Flota. Puedes configurar tus flotas de acuerdo a lo que hayas establecido antes de marchar."},
  [3031] = {id=3031,key="key_3031",data="Comandante, el registro a la Federación está por empezar. Sólo ingresa, asigna al miembro más fuerte y prepárate para la guerra."},
  [3032] = {id=3032,key="key_3032",data="Comandante, has alcanzado el lugar {1} en las clasificaciones de Héroes de las actividades legendarias, y no pudiste alcanzar a los mejores en esta ocasión. No te desanimes y sigue mejorando tus habilidades."},
  [3033] = {id=3033,key="key_3033",data="Felicidades por establecer tu ávatar. Asegúrate que no contenga material abusivo, violento, erótico, político, racista o de extremista religioso. Si tu ávatar no cumple las reglas, tomaremos acción y lo removeremos. Como medida preventiva, no podrás actualizarlo en los siguientes 2 días. \nSi te preocupa no cumplir los requerimientos, puedes tocar sobre el botón de 'Remplazar imágen' para cancelar el ávatar que creaste."},
  [3034] = {id=3034,key="key_3034",data="Comandante, {1} apareció en ({2}). Por favor lleva a tus flotas para derrotarlo."},
  [3035] = {id=3035,key="key_3035",data="Comandante, {1} ({2}) es muy peligroso. Hemos fallado en derrotarlo, y ha escapado... no sabemos si obtuvo información valiosa de nuestra galaxia."},
  [3036] = {id=3036,key="key_3036",data="Debido a la gran diferencia de poder entre tú y tus enemigos, tus flotas han sido fácilmente derrotadas. No hay reporte de batalla a enviar."},
  [3037] = {id=3037,key="key_3037",data="Comandante: \nJefe, ¡éste es un día muy emocionante! El sistema del Mercader Galáctico ha abierto finalmente, puedes invertir en todos los tipos de ciudades. \n Obtén una posición en la ciudad-estado y obtén un efecto personal muy fuerte y un efecto de Federación. Dependiendo del tipo de efecto que quieras tener es en la ciudad en donde deberás invertir. Los efectos de buff están bajo tu control."},
  [3038] = {id=3038,key="key_2038",data="Felicitaciones por actualizar tu Fortaleza a Nivel 8"},
  [3039] = {id=3039,key="key_3038",data="Comandante: \nJefe, ¡éste es un día muy emocionante! Ya puedes construir el Centro de Comercio. \n Después de construir el Centro de Comercio podrás intercambiar recursos con aliados. Obsequia recursos a tus aliados o solicita ayuda de ellos."},
  [3040] = {id=3040,key="key_2039",data="Recompensa de reclutamiento"},
  [3041] = {id=3041,key="key_3039",data="Comandante:\n¡Bienvenido! Estamos honrados en que una leyenda se una a nosotros. ¡Espero te diviertas en grande!"},
  [3042] = {id=3042,key="key_2040",data="Información de venta de materiales"},
  [3043] = {id=3043,key="key_3040",data="Comandante:\nTu {1} se ha vendido en el Centro de Comercio y obtuviste {2} de acero. Por favor, revísalo."},
  [3044] = {id=3044,key="key_2041",data="Recompensas de invitación"},
  [3045] = {id=3045,key="key_3041",data="Comandante:\nGracias por aceptar la invitación de tus amigos y por unirte. Te deseamos un próspero futuro, y esperamos escribir tu nombre en letras doradas en este juego. ¡Diviértete a lo grande! \nPara que te familiarices más rápido, te enviaremos las siguientes recompensas:"},
  [3046] = {id=3046,key="key_3046",data="Comandante:\nLa mina de recursos que planeas ocupar está siendo recolectada por tus aliados, por lo que tus flotas acaban de retirarse. Por favor vigila su regreso."},
  [3047] = {id=3047,key="key_3047",data="Comandante:\n Tu {1} ({2},{3}) ha sido destruida por el enemigo en {4}. ¡Ve a reconstruirla!"},
  [3048] = {id=3048,key="key_3048",data="Comandante: \n El número de navíos ha excedido la capacidad del centro de reparación, por favor presta atención para actualizar el Centro de Servicio.\nAl construir un centro con tamaño razonable, y al ser derrotado en batalla, te ayudará a mantener solo daños y no destrucciones. El mantenimiento de naves dañadas requiere menos recursos y tiempo que el tener que reconstruirlos si fueran destruidos."},
  [3049] = {id=3049,key="key_3049",data="Comandante: \nGracias por comprar {1}-días {2}. Recibirás utilerías como recompensa en los próximos días. Recuerda revisar tu correo y recibirlos a tiempo. Mientras tanto, te enviaremos las siguientes recompensas de la primera compra. ¡Recuerda recibirlos también!"},
  [3050] = {id=3050,key="key_3050",data="Comandante: \nTu sobre rojo ha expirado y todavía contiene diamantes. Aquí están los diamantes restantes que no recibiste, ¡por favor revísalos!"}
}
return TMail