local DUpgradeGuide = {
  [1] = {id=1,title="指揮官等級",desc="提升指揮官等級可獲得強大增益屬性，還可獲得特殊技能",content="1.攻打戰役副本可獲得指揮官經驗。\n2.擊殺星際海盜可獲得指揮官經驗。\n3.幸運轉盤、日常任務、活動可獲得指揮官經驗。"},
  [2] = {id=2,title="文明等級",desc="提升文明等級可提升戰艦單支編隊上限",content="1.升級建築可獲得繁榮值，繁榮值達到文明升級要求時即可升級。"},
  [3] = {id=3,title="統帥等級",desc="提升統帥等級可提升戰艦單支編隊上限",content="1.升級統帥等級需要消耗統帥令，擊殺星際海盜可獲得統帥令。\n2.花費鑽石可快速購買大量的統帥令。"},
  [4] = {id=4,title="軍銜等級",desc="提升軍銜等級可提升戰艦單支編隊上限，還可獲得增益屬性",content="1.擊殺其他玩家的戰艦即可獲得軍功值，擊殺戰艦等級越高，獲得軍功值越多。\n2.軍功值達到軍銜要求時可提升軍銜等級。"},
  [5] = {id=5,title="VIP等級",desc="提升並啟動VIP，可獲得強大增益屬性和各種特權",content="1.升級VIP等級需要獲得VIP點數，日常任務、戰役、星際海盜都掉落VIP點數。\n2.花費鑽石可快速購買大量的VIP點數。\n3.只有啟動VIP後，VIP特權才會生效。"},
  [6] = {id=6,title="護衛艦戰力",desc="護衛艦可有效對抗敵人的截擊艦和空母艦",content="1.在戰艦工廠可以打造護衛艦，消耗鑽石可快速打造。\n2.升級護衛艦工廠可提升護衛艦的等級。"},
  [7] = {id=7,title="截擊艦戰力",desc="截擊艦可有效對抗敵人的巡洋艦和空母艦",content="1.在戰艦工廠可以打造截擊艦，消耗鑽石可快速打造。\n2.升級截擊艦工廠可提升截擊艦的等級。"},
  [8] = {id=8,title="巡洋艦戰力",desc="巡洋艦可有效對抗敵人的護衛艦和空母艦",content="1.在戰艦工廠可以打造巡洋艦，消耗鑽石可快速打造。\n2.升級巡洋艦工廠可提升巡洋艦的等級。"},
  [9] = {id=9,title="空母艦戰力",desc="空母艦可有效對抗敵人的防禦武器",content="1.在戰艦工廠可以打造空母艦，消耗鑽石可快速打造。\n2.升級空母艦工廠可提升空母艦的等級。"},
  [10] = {id=10,title="防禦戰力",desc="防禦武器可有效對抗敵人的護衛艦、截擊艦、巡洋艦",content="1.在戰備中心可以打造防禦武器，消耗鑽石可快速打造。\n2.升級戰備中心可提升防禦武器的等級。"},
  [11] = {id=11,title="發展科技",desc="發展科技主要可減少建築升級時間，損艦維修時間",content="1.研究發展科技需要消耗科技因數和四種基礎資源。\n2.科技因數主要通過擊殺星際海盜、日常任務寶箱獲得。\n3.花費鑽石可快速研究發展科技。"},
